CREATE OR REPLACE PROCEDURE gerar_atestado_medico ( nr_sequencia_p bigint, nm_usuario_p text, nr_atendimento_p bigint, cd_pessoa_fisica_p text) AS $body$
DECLARE

 
cd_medico_resp_w	varchar(10);
ds_atestado_w		varchar(32000);


BEGIN 
 
Select	cd_medico_resp 
into STRICT	cd_medico_resp_w 
from	atendimento_paciente 
where	nr_atendimento	= nr_atendimento_p;
 
select	ds_atestado 
into STRICT	ds_atestado_w 
from	atestado_paciente 
where	nr_sequencia	=	nr_sequencia_p;	
 
Insert into atestado_paciente( 
	nr_sequencia, 
	dt_atestado, 
	nr_atendimento, 
	dt_atualizacao, 
	nm_usuario, 
	cd_medico, 
	ds_atestado, 
	cd_pessoa_fisica, 
	nr_seq_tipo_atestado) 
SELECT	nextval('atestado_paciente_seq'), 
	clock_timestamp(), 
	nr_atendimento_p, 
	clock_timestamp(), 
	nm_usuario_p, 
	cd_medico_resp_w, 
	ds_atestado_w, 
	cd_pessoa_fisica_p, 
	nr_sequencia 
from	tipo_atestado 
where	nr_sequencia = nr_sequencia_p;
 
commit;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
