SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_atividades_intervencao ( nr_seq_prescr_p bigint, ds_lista_protocolo_p text, nm_usuario_p text) AS $body$
DECLARE

lista_informacao_w		varchar(4000);
tam_lista_w			bigint;
ie_pos_virgula_w		smallint;
ie_pos_traco_w			bigint;
lista_inf_w			varchar(100);
nr_seq_proc_w			bigint;
nr_seq_atividade_w		bigint;


BEGIN


lista_informacao_w	:= ds_lista_protocolo_p;

if (substr(lista_informacao_w,length(lista_informacao_w))	<> ',') then
	lista_informacao_w	:= lista_informacao_w ||',';
end if;



while(lista_informacao_w IS NOT NULL AND lista_informacao_w::text <> '') loop
	begin

	tam_lista_w			:= length(lista_informacao_w);
	ie_pos_virgula_w		:= position(',' in lista_informacao_w);
	ie_pos_traco_w			:= position('-' in lista_informacao_w);
	lista_inf_w			:= substr(lista_informacao_w,1,ie_pos_virgula_w - 1);

	if (ie_pos_virgula_w <> 0) then
		begin
		nr_seq_proc_w		:= substr(lista_inf_w,1,(ie_pos_traco_w - 1));
		lista_inf_w		:= substr(lista_inf_w,ie_pos_traco_w + 1,100);
		nr_seq_atividade_w	:= lista_inf_w;
		lista_informacao_w	:= substr(lista_informacao_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end;
	end if;


	if (nr_seq_proc_w	> 0) and (nr_seq_atividade_w	> 0) then
		begin

		insert into pe_prescr_proc_atividade(	nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_seq_prescr_proc,
							nr_seq_atividade)
				values (	nextval('pe_prescr_proc_atividade_seq'),
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							nr_seq_proc_w,
							nr_seq_atividade_w);
		end;
	end if;

	end;
end loop;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
