CREATE OR REPLACE PROCEDURE gerar_atualizacao_previsao ( nr_seq_ordem_p bigint, nm_usuario_exec_p text, nm_usuario_prev_p text, qt_min_prev_p bigint) AS $body$
BEGIN

if (coalesce(qt_min_prev_p::text, '') = '') then

	update	man_ordem_servico_exec
	set		nm_usuario_prev 	= nm_usuario_prev_p
	where	nr_seq_ordem		= nr_seq_ordem_p
	and		nm_usuario_exec		= nm_usuario_exec_p;

else
	insert into man_ordem_servico_exec(nr_sequencia,
						nr_seq_ordem,
						dt_atualizacao,
						nm_usuario,
						nm_usuario_exec,
						nm_usuario_prev,
						qt_min_prev)
			values (	nextval('man_ordem_servico_exec_seq'),
						nr_seq_ordem_p,
						clock_timestamp(),
						nm_usuario_prev_p,
						nm_usuario_exec_p,
						nm_usuario_prev_p,
						qt_min_prev_p);

end if;

commit;

end;
$body$
