CREATE OR REPLACE PROCEDURE gerar_atualizar_resumo_conta (nr_interno_conta_p bigint, nr_atendimento_p bigint) AS $body$
DECLARE

 
nr_atendimento_w	bigint;
nr_interno_conta_w	bigint;
ie_status_acerto_w	smallint;

 
c01 CURSOR FOR 
	SELECT	nr_atendimento, 
		nr_interno_conta, 
		ie_status_acerto 
	from	conta_paciente 
	where	nr_atendimento	 = nr_atendimento_p 
	
union
 
	SELECT	nr_atendimento, 
		nr_interno_conta, 
		ie_status_acerto 
	from	conta_paciente 
	where	nr_interno_conta = nr_interno_conta_p;

 

BEGIN 
 
open	c01;
loop 
fetch	c01 into 
	nr_atendimento_w, 
	nr_interno_conta_w, 
	ie_status_acerto_W;
EXIT WHEN NOT FOUND; /* apply on c01 */
		 
	if (ie_status_acerto_W = 1) then 
		CALL Atualizar_Resumo_Conta(nr_interno_conta_w, 2);
	end if;
 
end loop;
close c01;
 
END;
$body$
