	nr_sequencia_w,
	vl_original_w;
EXIT WHEN NOT FOUND; /* apply on c11 */
	begin

	select 	Obter_se_agrup_audit_ext(nr_seq_regra_audit_ext_p, nr_sequencia_w, 'M')
	into STRICT	ie_agrupar_w
	;

	insert into w_auditoria_externa(NR_SEQ_AUDITORIA,
		DT_ATUALIZACAO,
		NM_USUARIO,
		DT_ATUALIZACAO_NREC,
		NM_USUARIO_NREC,
		NR_SEQ_ITEM,
		IE_AGRUPA,
		IE_PROC_MAT)
	values (nr_seq_auditoria_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_sequencia_w,
		ie_agrupar_w,
		'M');

	commit;

	end;
end loop;
close c11;

