CREATE OR REPLACE PROCEDURE gerar_ausencia_prof_escala ( nr_seq_escala_p bigint, cd_profissional_p text, dt_inicio_p timestamp, dt_fim_p timestamp, nr_seq_motivo_p bigint, nm_usuario_p text, cd_pessoa_substituta_p text default '', nr_seq_escala_adic_p bigint default 0) AS $body$
DECLARE


nr_seq_escala_w		bigint;
ie_tipo_escala_w		varchar(15);
qt_reg_w			bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		'D' ie_tipo_escala
	from	escala_diaria a
	where	a.dt_inicio between dt_inicio_p and fim_dia(dt_fim_p)
	and	a.nr_seq_escala = nr_seq_escala_p
	and	a.cd_pessoa_fisica = cd_profissional_p
	and	coalesce(a.nr_seq_motivo_afast::text, '') = ''
	and	trunc(a.dt_inicio) >= trunc(clock_timestamp())
	and	coalesce(nr_seq_escala_adic_p,0) = 0
	
union all

	SELECT	a.nr_sequencia,
		'A' ie_tipo_escala
	from	escala_diaria_adic b,
		escala_diaria a
	where	a.nr_sequencia = b.nr_seq_escala_diaria
	and	a.dt_inicio between dt_inicio_p and fim_dia(dt_fim_p)
	and	a.nr_seq_escala = nr_seq_escala_p
	and	b.cd_pessoa_fisica = cd_profissional_p
	and	coalesce(b.nr_seq_motivo_afast::text, '') = ''
	and	trunc(a.dt_inicio) >= trunc(clock_timestamp())
	and	b.nr_sequencia = nr_seq_escala_adic_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_escala_w,
	ie_tipo_escala_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(cd_pessoa_substituta_p,'X') <> 'X') then
		begin
		select 	count(*)
		into STRICT	qt_reg_w
		from	escala_diaria_adic
		where  	cd_pessoa_fisica = cd_pessoa_substituta_p
		and	nr_seq_escala_diaria = nr_seq_escala_w;

		if (qt_reg_w = 0) then
			insert into escala_diaria_adic(
						cd_pessoa_fisica,
						ds_observacao,
						dt_atualizacao,
						dt_atualizacao_nrec,
						nm_usuario,
						nm_usuario_nrec,
						nr_seq_escala_diaria,
						nr_sequencia)
					values (cd_pessoa_substituta_p,
						substr(CASE WHEN ie_tipo_escala_w='A' THEN wheb_mensagem_pck.get_texto(340140)  ELSE wheb_mensagem_pck.get_texto(306297) END ,1,255),
						clock_timestamp(),
						clock_timestamp(),
						nm_usuario_p,
						nm_usuario_p,
						nr_seq_escala_w,
						nextval('escala_diaria_adic_seq'));
		end if;
		end;
	end if;

	if (ie_tipo_escala_w = 'A') then
		update	escala_diaria_adic
		set	dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			ds_observacao		= substr(ds_observacao || chr(13) || chr(10) || wheb_mensagem_pck.get_texto(306298),1,255), --Atualizada a ausência do prof.
			nr_seq_motivo_afast	= nr_seq_motivo_p
		where	nr_sequencia		= nr_seq_escala_adic_p;
	else
		update	escala_diaria
		set	dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p,
			ds_observacao		= substr(ds_observacao || chr(13) || chr(10) || wheb_mensagem_pck.get_texto(306298),1,255), --Atualizada a ausência do prof.
			nr_seq_motivo_afast	= nr_seq_motivo_p
		where	nr_sequencia		= nr_seq_escala_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
