CREATE OR REPLACE PROCEDURE gerar_autorizacao_mat_pls (cd_estabelecimento_p bigint, nm_usuario_p text, nr_sequencia_autor_p bigint, nr_seq_mat_autor_p bigint) AS $body$
DECLARE


nr_seq_guia_plano_w	bigint;
cd_material_w		varchar(255);
ds_material_w		varchar(255);
qt_solicitada_w		bigint;
cd_edicao_amb_w		varchar(10);
nr_seq_guia_mat_w	bigint;


BEGIN

select	nr_seq_guia_plano
into STRICT	nr_seq_guia_plano_w
from	autorizacao_convenio a
where	a.nr_sequencia	= nr_sequencia_autor_p;

if (nr_seq_guia_plano_w IS NOT NULL AND nr_seq_guia_plano_w::text <> '') then

	select	coalesce(cd_material_convenio,cd_material),
		cd_edicao_amb,
		ds_material,
		qt_solicitada
	into STRICT	cd_material_w,
		cd_edicao_amb_w,
		ds_material_w,
		qt_solicitada_w
	from	tiss_material_solicitado_v
	where	nr_sequencia_autor	= nr_sequencia_autor_p
	and	nr_seq_mat_autor	= nr_seq_mat_autor_p
	and	ds_versao		= '2.01.01'
	and	ie_origem		= 'AC';

	select	max(nr_sequencia)
	into STRICT	nr_seq_guia_mat_w
	from	pls_guia_plano_mat
	where	nr_seq_guia	= nr_seq_guia_plano_w
	and	cd_material_imp	= cd_material_w;

	if (coalesce(nr_seq_guia_mat_w::text, '') = '') then
		CALL pls_mat_guia_prestador(	nr_seq_guia_plano_w,
					cd_material_w,
					ds_material_w,
					qt_solicitada_w,
					cd_edicao_amb_w,
					nr_seq_mat_autor_p,
					nm_usuario_p);
	else
		update	pls_guia_plano_mat
		set	qt_solicitada_imp		= qt_solicitada_w
		where	nr_seq_guia_mat_w	= nr_seq_guia_mat_w;

	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
