CREATE OR REPLACE PROCEDURE gerar_autorizacao_proc_pls (cd_estabelecimento_p bigint, nm_usuario_p text, nr_sequencia_autor_p bigint, nr_seq_proc_autor_p bigint) AS $body$
DECLARE

			 
nr_seq_guia_plano_w	bigint;
cd_procedimento_w	varchar(255);
ds_procedimento_w	varchar(255);
qt_solicitada_w		bigint;
cd_edicao_amb_w		varchar(10);
nr_seq_guia_proc_w	bigint;

 

BEGIN 
 
select	max(nr_seq_guia_plano) 
into STRICT	nr_seq_guia_plano_w	 
from	autorizacao_convenio a 
where	a.nr_sequencia	= nr_sequencia_autor_p;
 
if (nr_seq_guia_plano_w IS NOT NULL AND nr_seq_guia_plano_w::text <> '') then 
 
	select	max(coalesce(a.cd_procedimento_convenio, a.cd_procedimento)), 
		max(a.ds_procedimento), 
		max(a.qt_solicitada),		 
		max(a.cd_edicao_relat) 
	into STRICT	cd_procedimento_w, 
		ds_procedimento_w, 
		qt_solicitada_w, 
		cd_edicao_amb_w 
	from	tiss_proc_solicitado_autor_v a 
	where	a.nr_sequencia_autor	= nr_sequencia_autor_p 
	and	a.nr_seq_proc_autor	= nr_seq_proc_autor_p 
	and	a.ds_versao		= '2.01.01';
 
	select	max(nr_sequencia) 
	into STRICT	nr_seq_guia_proc_w 
	from	pls_guia_plano_proc 
	where	nr_seq_guia		= nr_seq_guia_plano_w 
	and	cd_procedimento_imp	= cd_procedimento_w;	
 
	if (coalesce(nr_seq_guia_proc_w::text, '') = '') then	 
		CALL pls_proc_guia_prestador(	nr_seq_guia_plano_w, 
					ds_procedimento_w, 
					cd_procedimento_w, 
					qt_solicitada_w, 
					cd_edicao_amb_w, 
					nr_seq_proc_autor_p, 
					nm_usuario_p);
	else 
		update	pls_guia_plano_proc 
		set	qt_solicitada_imp	= qt_solicitada_w 
		where	nr_sequencia	= nr_seq_guia_proc_w;	
	end if;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
