CREATE OR REPLACE PROCEDURE gerar_autor_opme_agenda_after (nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
					 
nr_seq_opme_w	bigint;					
					 
C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	agenda_pac_opme a 
	where	a.nr_seq_agenda = nr_seq_agenda_p 
	and	coalesce(a.nr_seq_motivo_exclusao::text, '') = '' 
	and	not exists (	SELECT 	1 
				from 	material_autorizado x 
				where	x.nr_seq_opme = a.nr_sequencia) 
	order by 1;					
					 

BEGIN 
 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_opme_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (coalesce(nr_seq_opme_w,0) <> 0) then 
		CALL gerar_autor_regra(null, 
			null, 
			null, 
			null, 
			null, 
			null, 
			'AP', 
			nm_usuario_p, 
			nr_seq_agenda_p, 
			null, 
			null, 
			null, 
			nr_seq_opme_w, 
			null, 
			'', 
			'', 
			'');
	end if;
	end;
end loop;
close C01;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
