CREATE OR REPLACE PROCEDURE gerar_avaliacao_cpoe (nr_seq_tipo_avaliacao_p bigint, nr_atendimento_p bigint, cd_medico_p text, cd_pessoa_fisica_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_cpoe_p bigint, ie_tipo_item_p text) AS $body$
DECLARE



nr_seq_avaliacao_w	med_avaliacao_paciente.nr_sequencia%type;
				

BEGIN

if ((nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') and (nr_seq_tipo_avaliacao_p IS NOT NULL AND nr_seq_tipo_avaliacao_p::text <> '')) then
	begin
	
	select	nextval('med_avaliacao_paciente_seq')
	into STRICT	nr_seq_avaliacao_w
	;	
	
	insert	into med_avaliacao_paciente(
					cd_estabelecimento,
					cd_medico,
					cd_perfil_ativo,
					cd_pessoa_fisica,
					dt_atualizacao,
					dt_atualizacao_nrec,
					dt_avaliacao,
					ie_apresentar_web,
					ie_avaliacao_parcial,
					ie_avaliador_aux,
					ie_restricao_visualizacao,
					ie_situacao,
					nm_usuario,
					nm_usuario_nrec,
					nr_atendimento,
					nr_seq_mat_cpoe,
					nr_seq_proc_cpoe, 
					nr_seq_hemo_cpoe,
					nr_seq_tipo_avaliacao,
					nr_sequencia)
		values (
					cd_estabelecimento_p,
					cd_medico_p,
					cd_perfil_p,
					cd_pessoa_fisica_p,
					clock_timestamp(),
					clock_timestamp(),
					clock_timestamp(),
					'N',
					'N',
					'N',
					'T',
					'A',
					nm_usuario_p,
					nm_usuario_p,
					nr_atendimento_p,
					CASE WHEN ie_tipo_item_p='M' THEN  nr_seq_cpoe_p  ELSE null END ,
					CASE WHEN ie_tipo_item_p='P' THEN  nr_seq_cpoe_p  ELSE null END ,
					CASE WHEN ie_tipo_item_p='H' THEN  nr_seq_cpoe_p  ELSE null END ,
					nr_seq_tipo_avaliacao_p,
					nr_seq_avaliacao_w);
	
	commit;

	exception
	when others then
		nr_seq_avaliacao_w	:= null;
	end;
	
end if;

end;
$body$
