SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_avaliacao_lote ( cd_medico_p text, ds_observacao_p text, dt_avaliacao_p timestamp, ie_apresentar_web_p text, ie_situacao_p text, nr_seq_tipo_avaliacao_p bigint, nr_seq_lote_p bigint, nm_usuario_p text, ie_opcao_p text, ds_lista_destino_p text ) AS $body$
DECLARE

 
 
cd_pessoa_fisica_w       varchar(10);
ds_lista_destino_w				varchar(2000);
nm_usuario_w					varchar(15);

 
c01 CURSOR FOR 
	SELECT p.cd_pessoa_fisica 
	from  usuario_v u, 
			pessoa_fisica p 
	where (u.cd_pessoa_fisica = p.cd_pessoa_fisica) 
	and		((ie_opcao_p = 'F') or (obter_se_contido_char(u.nm_usuario, ds_lista_destino_w) = 'S')) 
	and   ((ie_opcao_p <> 'F') or (p.ie_funcionario = 'S')) 
	order by p.cd_pessoa_fisica;

 
c02 CURSOR FOR 
	SELECT 	nm_usuario 
	from 	usuario_perfil 
	where 	obter_se_contido_char(cd_perfil, ds_lista_destino_p) = 'S' 
	group 	by nm_usuario 
	order 	by nm_usuario;	
 
 

BEGIN 
 
ds_lista_destino_w := '';
 
--Perfil 
if (ie_opcao_p = 'P') then 
 
	open c02;
	loop 
	fetch c02 into 
			nm_usuario_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin 
	 
		ds_lista_destino_w := ds_lista_destino_w || nm_usuario_w || ',';
	 
		end;
	end loop;
	close c02;
	 
	 
 
else  --usuario 
	ds_lista_destino_w := ds_lista_destino_p;
end if;
 
 
 
if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') and (dt_avaliacao_p IS NOT NULL AND dt_avaliacao_p::text <> '') then 
 
 
	open c01;
	loop 
	fetch c01 into 
		cd_pessoa_fisica_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
 
		insert into med_avaliacao_paciente( 
					nr_sequencia, 
					cd_medico, 
					cd_pessoa_fisica, 
					ds_observacao, 
					dt_avaliacao, 
					ie_apresentar_web, 
					ie_situacao, 
					nr_seq_tipo_avaliacao, 
					nm_usuario, 
					dt_atualizacao, 
					nr_seq_lote, 
					ie_Avaliacao_parcial 
					) 
		values ( 
					nextval('med_avaliacao_paciente_seq'), 
					cd_medico_p, 
					cd_pessoa_fisica_w, 
					ds_observacao_p, 
					dt_avaliacao_p, 
					ie_apresentar_web_p, 
					ie_situacao_p, 
					nr_seq_tipo_avaliacao_p, 
					nm_usuario_p, 
					clock_timestamp(), 
					nr_seq_lote_p, 
					'N' 
					);
 
 
		end;
	end loop;
	close c01;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
