CREATE OR REPLACE PROCEDURE gerar_avaliacao_medicamento ( nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_tipo_avaliacao_p bigint, nm_usuario_p text, cd_perfil_p bigint, nr_seq_avaliacao_p INOUT bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_pessoa_fisica_w	varchar(10);
cd_medico_w		varchar(10);
nr_atendimento_w	bigint;



BEGIN

select	max(nr_atendimento),
	max(cd_pessoa_fisica)
into STRICT	nr_atendimento_w,
	cd_pessoa_fisica_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

select	max(cd_pessoa_fisica)
into STRICT	cd_medico_w
from	usuario
where	nm_usuario	= nm_usuario_p;

select	nextval('med_avaliacao_paciente_seq')
into STRICT	nr_sequencia_w
;

insert into	med_avaliacao_paciente(nr_sequencia,
					cd_pessoa_fisica,
					cd_medico,
					dt_atualizacao,
					dt_avaliacao,
					nm_usuario,
					nr_seq_tipo_avaliacao,
					nr_atendimento,
					nr_prescricao,
					ie_situacao,
					cd_perfil_ativo,
					nr_seq_material,
					dt_atualizacao_nrec,
					nm_usuario_nrec)
values (nr_sequencia_w,
		cd_pessoa_fisica_w,
		cd_medico_w,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nr_Seq_tipo_avaliacao_p,
		nr_atendimento_w,
		nr_prescricao_p,
		'A',
		cd_perfil_p,
		nr_seq_material_p,
		clock_timestamp(),
		nm_usuario_p);

commit;

nr_seq_avaliacao_p	:= nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
