CREATE OR REPLACE PROCEDURE gerar_avaliacao_rep ( nr_prescricao_p bigint, nr_seq_tipo_avaliacao_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_medico_w		med_avaliacao_paciente.cd_medico%type;
cd_pessoa_fisica_w	med_avaliacao_paciente.cd_pessoa_fisica%type;
nr_atendimento_w	med_avaliacao_paciente.nr_atendimento%type;
nr_seq_avaliacao_w	med_avaliacao_paciente.nr_sequencia%type;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_tipo_avaliacao_p IS NOT NULL AND nr_seq_tipo_avaliacao_p::text <> '') then
	begin

	select	max(cd_medico),
		max(cd_pessoa_fisica),
		max(nr_atendimento)
	into STRICT	cd_medico_w,
		cd_pessoa_fisica_w,
		nr_atendimento_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	select	nextval('med_avaliacao_paciente_seq')
	into STRICT	nr_seq_avaliacao_w
	;

	insert	into med_avaliacao_paciente(
					CD_ESTABELECIMENTO,
					CD_MEDICO,
					CD_PERFIL_ATIVO,
					CD_PESSOA_FISICA,
					DT_ATUALIZACAO,
					DT_ATUALIZACAO_NREC,
					DT_AVALIACAO,
					IE_APRESENTAR_WEB,
					IE_AVALIACAO_PARCIAL,
					IE_AVALIADOR_AUX,
					IE_RESTRICAO_VISUALIZACAO,
					IE_SITUACAO,
					NM_USUARIO,
					NM_USUARIO_NREC,
					NR_ATENDIMENTO,
					NR_PRESCRICAO,
					NR_SEQ_TIPO_AVALIACAO,
					NR_SEQUENCIA)
		values (
					cd_estabelecimento_p,
					cd_medico_w,
					cd_perfil_p,
					cd_pessoa_fisica_w,
					clock_timestamp(),
					clock_timestamp(),
					clock_timestamp(),
					'N',
					'N',
					'N',
					'T',
					'A',
					nm_usuario_p,
					nm_usuario_p,
					nr_atendimento_w,
					nr_prescricao_p,
					nr_seq_tipo_avaliacao_p,
					nr_seq_avaliacao_w);

	commit;

	exception
	when others then
		nr_seq_avaliacao_w	:= null;
	end;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
