CREATE OR REPLACE PROCEDURE gerar_avaliacao_rh ( nr_seq_ordem_p text, nr_seq_tipo_aval_p bigint, cd_estabelecimento_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ava_w		bigint;
cd_pessoa_fisica_w	varchar(50);
nr_registros_w		bigint := 0;


BEGIN

select	a.cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_w
from	usuario	a
where	a.nm_usuario = nm_usuario_p;


select	nextval('man_pesquisa_rh_seq')
into STRICT	nr_seq_ava_w
;

select	count(*)
into STRICT	nr_registros_w
from	man_pesquisa_rh a
where	a.nr_seq_ordem = nr_seq_ordem_p;


if	(nr_seq_tipo_aval_p > 0 AND nr_registros_w = 0) then
begin
	insert	into man_pesquisa_rh(
		nr_seq_ordem,
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_pessoa_fisica,
		nr_seq_tipo_aval)
	values (nr_seq_ordem_p,
		nr_seq_ava_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		cd_pessoa_fisica_w,
		nr_seq_tipo_aval_p);
end;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
