CREATE OR REPLACE PROCEDURE gerar_aviso_agenda_paciente (cd_agenda_p bigint, dt_aviso_p timestamp, ds_aviso_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;
dt_atualizacao_w	timestamp := clock_timestamp();
dt_aviso_w		timestamp;


BEGIN

dt_aviso_w	:= ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_aviso_p);

if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_aviso_w IS NOT NULL AND dt_aviso_w::text <> '') then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	agenda_aviso
	where	cd_agenda	= cd_agenda_p
	and	dt_aviso	= dt_aviso_w;
	
	if (ds_aviso_p IS NOT NULL AND ds_aviso_p::text <> '') then

		if (nr_sequencia_w > 0) then

			update	agenda_aviso
			set	ds_aviso		= ds_aviso_p,
				nm_usuario		= nm_usuario_p,
				dt_atualizacao	= dt_atualizacao_w
			where	nr_sequencia		= nr_sequencia_w;

		else	
			select	nextval('agenda_aviso_seq')
			into STRICT	nr_sequencia_w
			;

			insert	into	agenda_aviso(
							nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							dt_aviso,
							ds_aviso,
							cd_agenda
							)
						values (
							nr_sequencia_w,
							dt_atualizacao_w,
							nm_usuario_p,
							dt_atualizacao_w,
							nm_usuario_p,
							dt_aviso_w,
							substr(ds_aviso_p,1,2000),
							cd_agenda_p
							);
		end if;

	elsif (nr_sequencia_w > 0) then

		delete	from agenda_aviso
		where	nr_sequencia = nr_sequencia_w;
	end if;		
end if;
COMMIT;
END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
