CREATE OR REPLACE PROCEDURE gerar_aviso_agenda_periodo (cd_agenda_p bigint, dt_aviso_p timestamp, dt_final_p timestamp, ds_aviso_p text, nm_usuario_p text) AS $body$
DECLARE


dt_aviso_w	timestamp;


BEGIN
if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_aviso_p IS NOT NULL AND dt_aviso_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') and (dt_final_p > dt_aviso_p) then
