SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_aviso_geral_agenda (nr_sequencia_p bigint, ds_aviso_p text, nm_usuario_p text) AS $body$
BEGIN
begin
	if (ds_aviso_p IS NOT NULL AND ds_aviso_p::text <> '') then

		if (coalesce(nr_sequencia_p,0) > 0) then

			update	agenda_aviso_geral
			set	ds_aviso		= ds_aviso_p,
				nm_usuario		= nm_usuario_p,
				dt_atualizacao		= clock_timestamp()
			where	nr_sequencia		= nr_sequencia_p;

		else

			insert	into	agenda_aviso_geral(
							nr_sequencia,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							ds_aviso)
						values (nextval('agenda_aviso_geral_seq'),
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							substr(ds_aviso_p,1,2000));
		end if;

	elsif (coalesce(nr_sequencia_p,0) > 0)  then

		delete	from agenda_aviso_geral
		where	nr_sequencia = nr_sequencia_p;
	end if;
commit;
exception
when others then
null;
end;
END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
