SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_baixa_ret_grg (nr_seq_retorno_p bigint, dt_baixa_p timestamp, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


nr_seq_ret_item_w	bigint;
vl_pago_w		double precision;
vl_glosado_w		double precision;
vl_amenor_w		double precision;
vl_adicional_w		double precision;
nr_seq_hist_guia_w	bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	sum(coalesce(a.vl_pago,0)),
	sum(coalesce(a.vl_glosado,0)),
	sum(coalesce(a.vl_amenor,0)),
	sum(coalesce(a.vl_adicional,0))
from	convenio_retorno_item a
where	a.nr_seq_retorno	= nr_seq_retorno_p
and (coalesce(a.vl_pago,0)	<> 0 or
	coalesce(a.vl_glosado,0)	<> 0 or
	coalesce(a.vl_amenor,0) 	<> 0 or
	coalesce(a.vl_adicional,0)	<> 0)
group by a.nr_sequencia;

c02 CURSOR FOR
SELECT	b.nr_sequencia
from	lote_audit_hist c,
	lote_audit_hist_guia b,
	convenio_retorno_item a
where	a.nr_sequencia		= nr_seq_ret_item_w
and	a.nr_interno_conta	= b.nr_interno_conta
and	a.cd_autorizacao	= b.cd_autorizacao
and	b.nr_seq_lote_hist	= c.nr_sequencia
and	(c.dt_fechamento IS NOT NULL AND c.dt_fechamento::text <> '')
and	c.nr_sequencia		= obter_ultima_analise(c.nr_seq_lote_audit);


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_ret_item_w,
	vl_pago_w,
	vl_glosado_w,
	vl_amenor_w,
	vl_adicional_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	open C02;
	loop
	fetch C02 into
		nr_seq_hist_guia_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		CALL gerar_baixa_lote_grg(	nr_seq_ret_item_w,
					nr_seq_hist_guia_w,
					vl_pago_w + vl_adicional_w,
					vl_glosado_w,
					vl_amenor_w,
					0,
					nm_usuario_p,
					dt_baixa_p);

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

if (ie_commit_p = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
