CREATE OR REPLACE PROCEDURE gerar_baixa_visit_acomp ( dt_parametro_p timestamp ) AS $body$
DECLARE


nr_atendimento_w	atendimento_paciente.nr_atendimento%type;

C01 CURSOR FOR
	SELECT 	nr_atendimento
	from 	atendimento_paciente
	where 	(dt_saida_real IS NOT NULL AND dt_saida_real::text <> '')
	and 	dt_saida_real >= dt_parametro_p - 1;


BEGIN
open C01;
loop
fetch C01 into	
	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then
	
	begin
		update	atendimento_visita
		set	dt_saida = dt_parametro_p,
			nm_usuario = 'JOB',
			ie_status = 'F',
			nm_usuario_saida = 'JOB',
			dt_atualizacao = dt_parametro_p
		where 	nr_atendimento = nr_atendimento_w
		and	coalesce(dt_saida::text, '') = '';
		
		update atendimento_acompanhante
		set	dt_saida = dt_parametro_p,
			nm_usuario = 'JOB',
			dt_atualizacao = dt_parametro_p
		where	nr_atendimento = nr_atendimento_w
		and	coalesce(dt_saida::text, '') = '';	
	
		if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
	exception
	when others then
		nr_atendimento_w := null;	
	end;
	
	end if;
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
