CREATE OR REPLACE PROCEDURE gerar_balanco_hidrico_pepo ( nr_cirurgia_p bigint, nr_seq_pepo_p bigint, nm_usuario_p text, nr_atendimento_p bigint ) AS $body$
DECLARE

 
qt_volume_w		double precision;
nr_seq_tipo_pg_w	bigint;
cd_profissional_w	varchar(10);

c01 CURSOR FOR 
SELECT	sum(coalesce(obter_conversao_ml(a.cd_material, a.qt_dose_total, a.cd_unidade_medida),0)), 
	nr_seq_tipo_pg 
from	agente_anestesico b, 
	w_resumo_agente a 
where	a.nr_seq_agente	= b.nr_sequencia 
and	(nr_seq_tipo_pg IS NOT NULL AND nr_seq_tipo_pg::text <> '') 
and	a.nr_cirurgia	= nr_cirurgia_p 
group by nr_seq_tipo_pg 

union
 
SELECT	sum(coalesce(obter_conversao_ml(a.cd_material, a.qt_dose_total, a.cd_unidade_medida),0)), 
	nr_seq_tipo_pg 
from	agente_anestesico b, 
	w_resumo_agente a 
where	a.nr_seq_agente	= b.nr_sequencia 
and	(nr_seq_tipo_pg IS NOT NULL AND nr_seq_tipo_pg::text <> '') 
and	a.nr_seq_pepo	= nr_seq_pepo_p 
group by nr_seq_tipo_pg 

union
 
select	sum(b.qt_dose), 
	c.nr_seq_tipo_pg 
from	cirurgia_agente_anestesico a, 
	cirurgia_agente_anest_ocor b, 
	san_derivado c 
where	a.nr_sequencia 		= b.nr_seq_cirur_agente 
and	a.nr_seq_derivado	= c.nr_sequencia 
and	((a.nr_cirurgia 	= nr_cirurgia_p) or (a.nr_seq_pepo = nr_seq_pepo_p)) 
and	c.ie_situacao		= 'A' 
and	coalesce(a.ie_situacao,'A')	= 'A' 
and	coalesce(b.ie_situacao,'A')	= 'A' 
group by nr_seq_tipo_pg;


BEGIN 
 
select	obter_dados_usuario_opcao(nm_usuario_p,'C') 
into STRICT	cd_profissional_w
;
 
delete	from atendimento_perda_ganho 
where	nr_cirurgia	=	nr_cirurgia_p 
and	ie_origem	=	'S';
 
delete	from atendimento_perda_ganho 
where	nr_seq_pepo	=	nr_seq_pepo_p 
and	ie_origem	=	'S';
commit;
 
OPEN C01;
LOOP 
FETCH C01 into	 
	qt_volume_w, 
	nr_seq_tipo_pg_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	if (qt_volume_w > 0) and (nr_seq_tipo_pg_w IS NOT NULL AND nr_seq_tipo_pg_w::text <> '') then 
		insert into atendimento_perda_ganho( 
			nr_sequencia, 
			nr_atendimento, 
			dt_atualizacao, 
			nm_usuario, 
			nr_seq_tipo, 
			qt_volume, 
			dt_medida, 
			cd_setor_atendimento, 
			nr_cirurgia, 
			ie_origem, 
			cd_profissional, 
			ie_situacao, 
			nr_seq_pepo) 
		values ( 
			nextval('atendimento_perda_ganho_seq'), 
			nr_atendimento_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_tipo_pg_w, 
			qt_volume_w, 
			clock_timestamp(), 
			obter_setor_atendimento(nr_atendimento_p), 
			nr_cirurgia_p, 
			'S', 
			cd_profissional_w, 
			'A', 
			nr_seq_pepo_p);
		commit;
	end if;
	end;
END LOOP;
CLOSE C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
