SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_bal_hidr_pepo_por_agente ( nr_cirurgia_p bigint, nr_seq_pepo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_atendimento_p bigint, nr_seq_agente_p bigint) AS $body$
DECLARE

 
qt_volume_w		double precision;
nr_seq_tipo_pg_w	bigint;
cd_profissional_w	varchar(10);
dt_liberacao_w		timestamp;
ie_gerar_liberados_w	varchar(1);
ie_tipo_w		smallint;

c01 CURSOR FOR 
	SELECT	sum(coalesce(obter_conversao_ml(a.cd_material, a.qt_dose_total, a.cd_unidade_medida),0)), 
		nr_seq_tipo_pg, 
		0 ie_tipo 
	from	agente_anestesico b, 
		w_resumo_agente a 
	where	a.nr_seq_agente	= b.nr_sequencia 
	and	(nr_seq_tipo_pg IS NOT NULL AND nr_seq_tipo_pg::text <> '') 
	and	((a.nr_cirurgia	= nr_cirurgia_p) or (a.nr_seq_pepo = nr_seq_pepo_p)) 
	and	a.nr_seq_agente = nr_seq_agente_p 
	group by nr_seq_tipo_pg 
	
union
 
	SELECT	sum(b.qt_dose), 
		c.nr_seq_tipo_pg, 
		ie_tipo 
	from	cirurgia_agente_anestesico a, 
		cirurgia_agente_anest_ocor b, 
		san_derivado c 
	where	a.nr_sequencia 		= b.nr_seq_cirur_agente 
	and	a.nr_seq_derivado	= c.nr_sequencia 
	and	((a.nr_cirurgia 	= nr_cirurgia_p) or (a.nr_seq_pepo = nr_seq_pepo_p)) 
	and	(c.nr_seq_tipo_pg IS NOT NULL AND c.nr_seq_tipo_pg::text <> '') 
	and	c.ie_situacao		= 'A' 
	and	coalesce(a.ie_situacao,'A')	= 'A' 
	and	coalesce(b.ie_situacao,'A')	= 'A' 
	and	a.nr_seq_derivado	= nr_seq_agente_p 
	group by nr_seq_tipo_pg,ie_tipo;


BEGIN 
 
ie_gerar_liberados_w := Obter_Param_Usuario(872, 144, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_gerar_liberados_w);
 
select	obter_dados_usuario_opcao(nm_usuario_p,'C') 
into STRICT	cd_profissional_w
;
 
if (ie_gerar_liberados_w = 'S') then 
	dt_liberacao_w := clock_timestamp();
else	 
	dt_liberacao_w := null;
end if;		
 
OPEN C01;
LOOP 
FETCH C01 into	 
	qt_volume_w, 
	nr_seq_tipo_pg_w, 
	ie_tipo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	if (qt_volume_w > 0) and (nr_seq_tipo_pg_w IS NOT NULL AND nr_seq_tipo_pg_w::text <> '') then 
		insert into atendimento_perda_ganho( 
			nr_sequencia, 
			nr_atendimento, 
			dt_atualizacao, 
			nm_usuario, 
			nr_seq_tipo, 
			qt_volume, 
			dt_medida, 
			cd_setor_atendimento, 
			nr_cirurgia, 
			nr_seq_pepo, 
			ie_origem, 
			cd_profissional, 
			ie_situacao, 
			dt_liberacao) 
		values ( 
			nextval('atendimento_perda_ganho_seq'), 
			nr_atendimento_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_tipo_pg_w, 
			qt_volume_w, 
			clock_timestamp(), 
			obter_setor_atendimento(nr_atendimento_p), 
			nr_cirurgia_p, 
			nr_seq_pepo_p, 
			'S', 
			cd_profissional_w, 
			'A', 
			dt_liberacao_w);
		 
		if (ie_tipo_w = 5) then	 
			update	cirurgia_agente_anestesico 
			set	dt_balanco_hidrico 	= dt_liberacao_w 
			where	nr_seq_derivado  	= nr_seq_agente_p 
			and	((nr_cirurgia = nr_cirurgia_p) or (nr_seq_pepo = nr_seq_pepo_p)) 
			and	coalesce(ie_situacao,'A') = 'A';
		else 
			update	cirurgia_agente_anestesico 
			set	dt_balanco_hidrico 	= dt_liberacao_w 
			where	nr_seq_agente  	= nr_seq_agente_p 
			and	((nr_cirurgia = nr_cirurgia_p) or (nr_seq_pepo = nr_seq_pepo_p)) 
			and	coalesce(ie_situacao,'A') = 'A';
		end if;	
		commit;
	end if;
	end;
END LOOP;
CLOSE C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_bal_hidr_pepo_por_agente ( nr_cirurgia_p bigint, nr_seq_pepo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_atendimento_p bigint, nr_seq_agente_p bigint) FROM PUBLIC;

