CREATE OR REPLACE PROCEDURE gerar_bloqueto_parcial_total ( nr_titulo_p bigint, cd_banco_p bigint, ie_altera_tipo_bloqueto_p text, ie_tipo_baixa_neg_bloq_p text, ie_bloqueto_p text, ie_tipo_titulo_p text, nr_bloqueto_p text, nr_bloqueto_leitura_p text, vl_bloq_associado_p bigint, vl_bloq_desc_p bigint, vl_bloq_juros_p bigint, vl_dia_antecipacao_p bigint, vl_saldo_juros_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
DECLARE

qt_registros_w	bigint;

/*
IE_OPCAO define a ação - Utilizado no tasy Swing
*/
BEGIN



if (coalesce(ie_opcao_p, -1) = 1) then

	update	titulo_pagar
	set	ie_tipo_titulo		= CASE WHEN ie_altera_tipo_bloqueto_p='S' THEN  1  ELSE ie_tipo_titulo END ,
		vl_bloq_associado		= vl_bloq_associado_p,
		nr_bloqueto		= nr_bloqueto_p,
		nr_bloqueto_leitura	= nr_bloqueto_leitura_p,
		vl_bloq_juros		= vl_bloq_juros_p,
		vl_bloq_desc		= vl_bloq_desc_p,
		vl_dia_antecipacao	= vl_dia_antecipacao_p,
		vl_saldo_juros		= vl_saldo_juros_p,
		cd_tipo_baixa_neg		= coalesce(ie_tipo_baixa_neg_bloq_p, cd_tipo_baixa_neg),
		ie_bloqueto          		= 'S',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_titulo            		= nr_titulo_p;

	select	count(*)
	into STRICT	qt_registros_w
	from	banco
	where	cd_banco = cd_banco_p;

	if (qt_registros_w > 0) then

		update	titulo_pagar_escrit
		set	cd_banco	= cd_banco_p,
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_titulo		= nr_titulo_p
		and	coalesce(cd_banco::text, '') = '';

	end if;

elsif (coalesce(ie_opcao_p, -1) = 2) then

	update	titulo_pagar
	set	nr_bloqueto = nr_bloqueto_p
	where	nr_titulo = nr_titulo_p;

	select	count(*)
	into STRICT	qt_registros_w
	from	banco
	where	cd_banco	= cd_banco_p;

	if (qt_registros_w <> 0) then

		update	titulo_pagar_escrit
		set	cd_banco	= cd_banco_p,
			nm_usuario	= nm_usuario_P,
			dt_atualizacao	= clock_timestamp()
		where	nr_titulo	= nr_titulo_p
		and	coalesce(cd_banco::text, '') = '';

	end if;

elsif (coalesce(ie_opcao_p, -1) = 3) then

	update	titulo_pagar
	set	nr_bloqueto	= nr_bloqueto_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_titulo	= nr_titulo_p;

	select	count(*)
	into STRICT	qt_registros_w
	from	banco
	where	cd_banco = cd_banco_p;

	if (qt_registros_w <> 0) then

		update	titulo_pagar_escrit
		set	cd_banco		= cd_banco_p
		where	nr_titulo	= nr_titulo_p
		and coalesce(cd_banco::text, '') = '';
	end if;
end if;
commit;

end;
$body$
