CREATE OR REPLACE PROCEDURE gerar_boca_pes_fisica (cd_pessoa_fisica_p bigint, dt_aprov_contrato_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_pes_fis_boca_w		bigint;
dente_table_w			pls_util_cta_pck. t_varchar2_table_20;

C01 CURSOR FOR 
	SELECT	cd_dente 
	from	tiss_dente;


BEGIN 
 
insert into pes_fis_boca(nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			cd_pessoa_fisica, 
			dt_inicio_vigencia, 
			dt_fim_vigencia) 
values (nextval('pes_fis_boca_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			cd_pessoa_fisica_p, 
			dt_aprov_contrato_p, 
			null) returning nr_sequencia into nr_seq_pes_fis_boca_w;
			 
open C01;
fetch C01 bulk collect into	 
	dente_table_w;
	begin 
	 
	if (dente_table_w.count > 0) then 
	 
	forall i in dente_table_w.first..dente_table_w.last 
	insert into pes_fis_boca_dente(nr_sequencia, dt_atualizacao, nm_usuario, nr_seq_pes_fis_boca, cd_dente, cd_situacao_atual) 
	values (nextval('pes_fis_boca_dente_seq'), clock_timestamp(), nm_usuario_p, nr_seq_pes_fis_boca_w, dente_table_w(i), 'H');
 
	end if;
	end;
close C01;
 
--Não poderá conter commit pois esta procedure é chamada pela aprovação do contrato. 
 
end;
$body$
