CREATE OR REPLACE PROCEDURE gerar_boletim_ocor_sugestao (nr_seq_sugestao_p bigint, nm_usuario_p text, nr_seq_bol_ocor_p INOUT bigint) AS $body$
DECLARE


nr_seq_bol_ocor_w	bigint;
cd_estabelecimento_w	smallint;
nr_seq_classif_w	varchar(10);
qt_classif_w		bigint;


BEGIN

select	max(a.cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	sug_sugestao a
where	a.nr_sequencia	= nr_seq_sugestao_p;

select	nextval('sac_boletim_ocorrencia_seq')
into STRICT	nr_seq_bol_ocor_w
;

insert	into sac_boletim_ocorrencia(cd_estabelecimento,
	cd_pessoa_fisica,
	cd_pf_referencia,
	ds_ocorrencia,
	dt_atualizacao,
	dt_ocorrencia,
	ie_origem,
	nm_usuario,
	nr_seq_sugestao,
	nr_sequencia)
SELECT	a.cd_estabelecimento,
	a.cd_pessoa_fisica,
	a.cd_pessoa_destino,
	a.ds_sugestao,
	clock_timestamp(),
	a.dt_sugestao,
	'E',
	nm_usuario_p,
	a.nr_sequencia,
	nr_seq_bol_ocor_w
from	sug_sugestao a
where	a.nr_sequencia	= nr_seq_sugestao_p;

nr_seq_classif_w := obter_param_usuario(2500, 7, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, nr_seq_classif_w);

if (nr_seq_classif_w IS NOT NULL AND nr_seq_classif_w::text <> '') then

	select	count(*)
	into STRICT	qt_classif_w
	from	sug_classificacao a
	where	a.nr_sequencia	= (nr_seq_classif_w)::numeric;

	if (qt_classif_w > 0) then

		update	sug_sugestao
		set	nr_seq_classif	= (nr_seq_classif_w)::numeric
		where	nr_sequencia	= nr_seq_sugestao_p;

	end if;

end if;

nr_seq_bol_ocor_p	:= nr_seq_bol_ocor_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
