CREATE OR REPLACE PROCEDURE gerar_bras_control_import ( ie_status_consulta_p text, nm_usuario_p text) AS $body$
BEGIN

if (     ie_status_consulta_p = 'I') then

        begin
        delete  from BRASINDICE_CONTROL_IMPORT
        where   nm_usuario = nm_usuario_p
        or      nm_usuario_nrec = nm_usuario_p;
        commit;
        end;

        begin
        insert into BRASINDICE_CONTROL_IMPORT(    nr_sequencia,
                                                dt_atualizacao,
                                                dt_atualizacao_nrec,
                                                nm_usuario,
                                                nm_usuario_nrec,
                                                dt_inicio_consulta,
                                                dt_final_consulta,
                                                ie_status_consulta)
                                        values ( nextval('eis_conta_pend_controle_seq'),
                                                clock_timestamp(),
                                                clock_timestamp(),
                                                nm_usuario_p,
                                                nm_usuario_p,
                                                clock_timestamp(),
                                                null,
                                                ie_status_consulta_p);
        commit;
        end;

elsif (  ie_status_consulta_p = 'F') then

        begin
        update  BRASINDICE_CONTROL_IMPORT
        set     dt_final_consulta = clock_timestamp(),
                ie_status_consulta = ie_status_consulta_p
        where   nm_usuario = nm_usuario_p
        or      nm_usuario_nrec = nm_usuario_p;
        commit;
        end;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
