SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_cad_pf_checkup ( nr_cpf_p text, nm_usuario_p text, cd_pessoa_fisica_p INOUT text) AS $body$
DECLARE


qt_pf_w		bigint;
cd_pessoa_fisica_w	varchar(10);
ds_erro_w		varchar(2000);



BEGIN

begin
select	max(cd_pessoa_fisica)
into STRICT	cd_pessoa_fisica_w
from	pessoa_fisica
where	nr_cpf = nr_cpf_p;

if (coalesce(cd_pessoa_fisica_w::text, '') = '') then

	select	nextval('pessoa_fisica_seq')
	into STRICT	cd_pessoa_fisica_w
	;

	insert into pessoa_fisica(
			cd_pessoa_fisica,
			nm_pessoa_fisica,
			ie_tipo_pessoa,
			dt_atualizacao,
			nr_cpf,
			ie_sexo,
			dt_nascimento,
			nm_usuario)
		SELECT	cd_pessoa_fisica_w,
			nm_pessoa_fisica,
			2,
			clock_timestamp(),
			nr_cpf,
			ie_sexo,
			dt_nascimento,
			nm_usuario_p
		from	checkup_imp_pessoa
		where	nr_cpf = nr_cpf_p
		and	coalesce(cd_pessoa_fisica::text, '') = '';

end if;


update	checkup_imp_pessoa
set	cd_pessoa_fisica	= cd_pessoa_fisica_w
where	nr_cpf = nr_cpf_p
and	coalesce(cd_pessoa_fisica::text, '') = '';


exception
	when others then
	ds_erro_w	:= substr(SQLERRM(SQLSTATE),1,2000);

end;

cd_pessoa_fisica_p	:= cd_pessoa_fisica_w;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
