CREATE OR REPLACE PROCEDURE gerar_caixa_opme ( nr_seq_caixa_p bigint, nm_usuario_p text ) AS $body$
DECLARE



nr_sequencia_w	bigint;
nr_seq_tipo_w	bigint;


BEGIN

select	nextval('opme_caixa_item_seq')
into STRICT	nr_sequencia_w
;

select	nr_seq_tipo
into STRICT	nr_seq_tipo_w
from	opme_caixa
where	nr_sequencia	= nr_seq_caixa_p;

insert into opme_caixa_item(
	nr_sequencia,
	nr_seq_caixa,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	cd_material,
	qt_inicial,
	qt_saldo,
	nr_seq_marca)
SELECT	nextval('opme_caixa_item_seq'),
	nr_seq_caixa_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	a.cd_material,
	a.qt_item,
	a.qt_item,
	a.nr_seq_marca
from	opme_tipo_caixa_item a
where	a.nr_seq_tipo	=	nr_seq_tipo_w
and	coalesce(a.dt_exclusao::text, '') = ''
and	not exists (	select	1
			from	opme_caixa_item x
			where	x.nr_seq_caixa	=	nr_seq_caixa_p
			and	x.cd_material	=	a.cd_material );

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
