SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_cancelamento_cpi (nr_dias_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;

C01 CURSOR FOR
	SELECT nr_sequencia
	FROM SAME_CPI_SOLIC
	WHERE trunc(dt_desejada) <= trunc(clock_timestamp()) - nr_dias_p
	AND cd_estabelecimento = cd_estabelecimento_p
	and coalesce(DT_CANCELAMENTO::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	SAME_CPI_SOLIC
	set	DT_CANCELAMENTO        = clock_timestamp(),
		NM_USUARIO_CANCEL      = 'Job',
		DS_JUSTIFICATIVA_CANCELAMENTO	= wheb_mensagem_pck.get_texto(795857, 'DT_ATUAL='||to_char(clock_timestamp(), pkg_date_formaters.localize_mask('shortDate', pkg_date_formaters.getUserLanguageTag(cd_estabelecimento_p, wheb_usuario_pck.get_nm_usuario)))),
		IE_STATUS_SOLIC		= 'C'
	where	nr_sequencia = nr_sequencia_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
