CREATE OR REPLACE PROCEDURE gerar_cancelamento_exame ( nr_prescricao_p bigint, nr_seq_proced_p bigint, ds_obs_canc_exame_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


/*
ie_opcao_p					
'C' = Cancelar
'D' = Desfazer cancelamento
'J' = Justificar cancelamento
*/
dt_cancelamento_w	prescr_procedimento.dt_cancelamento%type;
ds_obs_canc_exame_w	prescr_procedimento.ds_observacao_canc_exame%type;
ie_suspender_exame_w	varchar(2);


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_proced_p IS NOT NULL AND nr_seq_proced_p::text <> '') then

	if (ie_opcao_p = 'C') or (ie_opcao_p = 'D') then
		begin
	
		if (ie_opcao_p = 'C') then
			dt_cancelamento_w := clock_timestamp();
		else
			dt_cancelamento_w := null;
		end if;
	
		if (ds_obs_canc_exame_p IS NOT NULL AND ds_obs_canc_exame_p::text <> '') then
			ds_obs_canc_exame_w	:= substr(ds_obs_canc_exame_p,1,255);
		else
			ds_obs_canc_exame_w	:= null;
		end if;

		ie_suspender_exame_w := obter_param_usuario(942, 361, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_suspender_exame_w);

		if (coalesce(ie_suspender_exame_w,'N') = 'S') and (ie_opcao_p = 'C') then
			CALL Suspender_item_Prescricao(nr_prescricao_p,nr_seq_proced_p,null,null,'PRESCR_PROCEDIMENTO',nm_usuario_p,'S',942,null);
		end if;

		update	prescr_procedimento
		set 	dt_cancelamento 		= dt_cancelamento_w,
			ds_observacao_canc_exame	= ds_obs_canc_exame_w,
			nm_usuario 			= nm_usuario_p
		where	nr_prescricao			= nr_prescricao_p
		and	nr_sequencia			= nr_seq_proced_p;

		end;	
	end if;
end if;
	
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
