SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_carga ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, cd_centro_controle_p bigint, cd_natureza_gasto_p bigint, vl_orcado_p text, nm_usuario_p text, nr_seq_tabela_p bigint, qt_dias_prazo_p bigint, ie_carga_p bigint default 0) AS $body$
DECLARE



cd_centro_controle_w	integer;
ie_tipo_gasto_w		varchar(2);
nr_seq_ng_w		bigint;


BEGIN

select	coalesce(max(cd_centro_controle),0)
into STRICT	cd_centro_controle_w
from	orcamento_custo
where	cd_estabelecimento = cd_estabelecimento_p
and	nr_seq_tabela		= nr_seq_tabela_p
and	cd_centro_controle	= cd_centro_controle_p
and	cd_natureza_gasto	= cd_natureza_gasto_p;

select	max(nr_sequencia)
into STRICT	nr_seq_ng_w
from	natureza_gasto
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_natureza_gasto	= cd_natureza_gasto_p;

--se ainda não existe o centro de custo - insere
if (cd_centro_controle_w = 0) then
	begin


	ie_tipo_gasto_w	:= substr(cus_obter_tipo_gasto_centro(cd_estabelecimento_p, cd_centro_controle_p, null, cd_natureza_gasto_p,nr_seq_ng_w),1,1);

	if (ie_tipo_gasto_w in ('V','F','R')) then
		insert into orcamento_custo(
			nr_sequencia,
			cd_estabelecimento,
			cd_tabela_custo,
			cd_centro_controle,
			cd_natureza_gasto,
			dt_atualizacao,
			nm_usuario,
			vl_orcado,
			qt_dias_prazo,
			vl_a_distribuir,
			ie_tipo_gasto,
			nr_seq_ng,
			nr_seq_tabela)
		values (	nextval('orcamento_custo_seq'),
			cd_estabelecimento_p,
			cd_tabela_custo_p,
			cd_centro_controle_p,
			cd_natureza_gasto_p,
			clock_timestamp(),
			nm_usuario_p,
			vl_orcado_p,
			qt_dias_prazo_p,
			0,
			ie_tipo_gasto_w,
			nr_seq_ng_w,
			nr_seq_tabela_p);
	end if;
	end;

else
begin
--se já existe o centro de custo verifica opção se vai somar com o valor já existente ou vai subtituir pelo novo valor que vem do arquivo
	if (ie_carga_p = 1) then --soma
		update orcamento_custo
		set	vl_orcado = vl_orcado + vl_orcado_p
		where	cd_centro_controle = cd_centro_controle_w
		and	nr_seq_ng = nr_seq_ng
		and	nr_seq_tabela = nr_seq_tabela_p
		and	cd_estabelecimento = cd_estabelecimento_p
		and	cd_natureza_gasto = cd_natureza_gasto_p;
	elsif (ie_carga_p = 0) then --substitui
		update orcamento_custo
		set	vl_orcado = vl_orcado_p
		where	cd_centro_controle = cd_centro_controle_w
		and	nr_seq_ng = nr_seq_ng
		and	nr_seq_tabela = nr_seq_tabela_p
		and	cd_estabelecimento = cd_estabelecimento_p
		and	cd_natureza_gasto = cd_natureza_gasto_p;
	end if;

end;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
