CREATE OR REPLACE PROCEDURE gerar_cartao_cr_dtef_parcela ( nr_seq_movto_p bigint, nm_usuario_p text, dt_primeira_parcela_p timestamp) AS $body$
DECLARE


vl_parcela_w			double precision;
vl_transacao_w			double precision;
vl_desconto_operadora_w		double precision;
qt_gerada_w			smallint	:= 0;
qt_parcelas_w			smallint;


BEGIN

select	coalesce(a.qt_parcela,0),
	a.vl_transacao,
	a.vl_desconto_operadora
into STRICT	qt_parcelas_w,
	vl_transacao_w,
	vl_desconto_operadora_w
from	movto_cartao_cr a
where	a.nr_sequencia	= nr_seq_movto_p;

vl_parcela_w:= trunc(coalesce(dividir_sem_round((vl_transacao_w-vl_desconto_operadora_w),qt_parcelas_w),0),2);

while(qt_gerada_w < qt_parcelas_w) loop

	insert into movto_cartao_cr_parcela(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_movto,
		dt_parcela,
		vl_parcela,
		vl_liquido,
		vl_saldo_liquido,
		vl_despesa)
	values (nextval('movto_cartao_cr_parcela_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_movto_p,
		clock_timestamp(),
		vl_parcela_w,
		vl_parcela_w,
		vl_parcela_w,
		0);

	qt_gerada_w	:= qt_gerada_w + 1;
end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
