SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_checklist_atendimento ( nm_usuario_p text, nr_sequencia_p bigint, cd_profissional_p text) AS $body$
DECLARE


nr_seq_checklist_w   		bigint;
nr_atendimento_w   		bigint;
cd_pessoa_fisica_w   		varchar(10);
nr_seq_avaliacao_w   		bigint;
nr_sequencia_w    		bigint;
nr_sequencia_avaliacao_w 	bigint;
nr_cirurgia_w			bigint;
nr_seq_pepo_w			bigint;

c01 CURSOR FOR
SELECT 	nr_seq_avaliacao,
	nr_sequencia
from 	checklist_processo_item
where 	nr_seq_checklist = nr_seq_checklist_w
order by 1;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	select  max(nr_seq_checklist),
		max(nr_atendimento),
		max(cd_pessoa_fisica),
		max(nr_cirurgia),
		max(nr_seq_pepo)
	into STRICT 	nr_seq_checklist_w,
		nr_atendimento_w,
		cd_pessoa_fisica_w,
		nr_cirurgia_w,
		nr_seq_pepo_w
	from 	atendimento_checklist
	where 	nr_sequencia = nr_sequencia_p;

	open c01;
	loop
	fetch c01 into
		nr_seq_avaliacao_w,
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		select  nextval('med_avaliacao_paciente_seq')
		into STRICT 	nr_sequencia_avaliacao_w
		;

		insert into 	med_avaliacao_paciente(nr_sequencia,
							cd_pessoa_fisica,
							cd_medico,
							dt_avaliacao,
							dt_atualizacao,
							nm_usuario,
							nr_seq_atend_checklist,
							nr_seq_checklist_item,
							nr_seq_tipo_avaliacao,
							nr_atendimento,
							dt_atualizacao_nrec,
							ie_atualiza_macro,
							nr_cirurgia,
							nr_seq_pepo)
		values (nr_sequencia_avaliacao_w,
							cd_pessoa_fisica_w,
							cd_profissional_p,
							clock_timestamp(),
							clock_timestamp(),
							nm_usuario_p,
							nr_sequencia_p,
							nr_sequencia_w,
							nr_seq_avaliacao_w,
							nr_atendimento_w,
							clock_timestamp(),
							'S',
							nr_cirurgia_w,
							nr_seq_pepo_w);
		end;
	end loop;
	close c01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
