SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_chegada_dose ( dt_referencia_p timestamp, nr_prescricao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_contador_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_contador_w
from	can_ordem_prod
where	nr_prescricao		= nr_prescricao_p
and	trunc(dt_prevista)	= trunc(dt_referencia_p)
and	coalesce(dt_entrega_setor::text, '') = '';

if (qt_contador_w = 0) then
	update	paciente_atendimento
	set	dt_dispensacao_medic	= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_prescricao		= nr_prescricao_p
	and	trunc(dt_prevista)	= trunc(dt_referencia_p);
end if;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
