SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_choque_septico (nr_atendimento_p bigint, nm_usuario_p text, nr_seq_escala_p text default '124', ie_regra_sepse_p text default 'CS', nr_sequencia_p bigint default 0) AS $body$
DECLARE


nr_seq_pend_regra_w 	bigint;
cd_pessoa_fisica_w      varchar(10);
qt_idade_w              bigint;
cd_setor_atendimento_w  bigint := null;
qt_idade_dias_w         integer;
qt_peso_gramas_w        double precision;
nr_sequencia_w          bigint;
ie_restricao_complementar_w varchar(80);
dt_liberacao_reaval_w   timestamp;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from    gqa_pendencia_regra a,
			GQA_ACAO b
	where   a.nr_sequencia = b.nr_seq_pend_regra
	and	    coalesce(a.ie_situacao,'A') = 'A'
	and     coalesce(b.ie_situacao,'A') = 'A'
	and     a.NR_SEQ_ESCALA = nr_seq_escala_p
	and     a.IE_REGRA_SEPSE = ie_regra_sepse_p
	and	    (b.nr_seq_evento IS NOT NULL AND b.nr_seq_evento::text <> '')
	and	    obter_se_gqa_regra_liberada(a.nr_sequencia) = 'S'
	and	    qt_idade_w       between coalesce(a.qt_idade_min,0) and coalesce(a.qt_idade_max,999)
	and     qt_idade_dias_w  between coalesce(a.qt_dias_min ,0) and coalesce(a.qt_dias_max ,99999)
	and     qt_peso_gramas_w between coalesce(a.qt_peso_gramas_min,0) and coalesce(a.qt_peso_gramas_max,999999999)
	and	    coalesce(a.cd_setor_atendimento,coalesce(cd_setor_atendimento_w,0)) = coalesce(cd_setor_atendimento_w,0);



BEGIN

if (ie_regra_sepse_p = 'RA') then
	select max(dt_liberacao_reaval)
	into STRICT   dt_liberacao_reaval_w
	from   escala_sepse_infantil
	where  nr_sequencia = nr_sequencia_p;
	
	if (dt_liberacao_reaval_w IS NOT NULL AND dt_liberacao_reaval_w::text <> '') then
		goto Final;
	end if;
end if;

cd_pessoa_fisica_w      := obter_pessoa_atendimento(nr_atendimento_p,'C');
cd_setor_atendimento_w	:= obter_setor_atendimento(nr_atendimento_p);
qt_idade_w		        := obter_idade_pf(cd_pessoa_fisica_w,clock_timestamp(),'A');
qt_idade_dias_w		    := obter_idade_pf(cd_pessoa_fisica_w, clock_timestamp(), 'DIA');
qt_peso_gramas_w		:= (coalesce(obter_peso_pf(cd_pessoa_fisica_w),0) * 1000);

open C01;
loop
fetch C01 into	
	nr_seq_pend_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select GQA_regra_complementar(nm_usuario_p, nr_atendimento_p, nr_seq_pend_regra_w)
	into STRICT ie_restricao_complementar_w
	;
		
	if ie_restricao_complementar_w = 'X' then
		select	nextval('gqa_pendencia_pac_seq')
		into STRICT	nr_sequencia_w
		;
		
		insert into gqa_pendencia_pac(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						cd_pessoa_fisica,
						nr_atendimento,
						nr_seq_pend_regra)
		values (	nr_sequencia_w,
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						cd_pessoa_fisica_w,
						nr_atendimento_p,
						nr_seq_pend_regra_w);
		commit;
		CALL gqa_gerar_acao_regra(nr_seq_pend_regra_w,nr_sequencia_w,nr_atendimento_p,cd_pessoa_fisica_w,nm_usuario_p);
	end if;
	
	end;
end loop;
close C01;

commit;

<<Final>>
nr_seq_pend_regra_w	:= 0;

end;

$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_choque_septico (nr_atendimento_p bigint, nm_usuario_p text, nr_seq_escala_p text default '124', ie_regra_sepse_p text default 'CS', nr_sequencia_p bigint default 0) FROM PUBLIC;

