CREATE OR REPLACE PROCEDURE gerar_ciclo_inventario ( nr_seq_regra_p bigint, dt_inicial_ciclo_p timestamp, qt_ciclos_p bigint, qt_dias_ciclos_p bigint, nm_usuario_p text) AS $body$
DECLARE


/*variaveis*/

i		bigint;
qt_ciclos_w	bigint;
qt_ciclos_gerar_w	bigint;
dt_inicial_ciclo_w	timestamp;
dt_final_ciclo_w	timestamp;


BEGIN
select	count(*)
into STRICT	qt_ciclos_w
from	sup_inv_ciclico_ciclo
where	nr_seq_regra = nr_seq_regra_p
and	trunc(dt_inicial_ciclo_p) between trunc(dt_inicio) and trunc(dt_fim);

if (qt_ciclos_w = 0) then
	qt_ciclos_gerar_w	:= round((qt_ciclos_p)::numeric,0);

	dt_inicial_ciclo_w	:= dt_inicial_ciclo_p;
	dt_final_ciclo_w	:= dt_inicial_ciclo_w + qt_dias_ciclos_p;

	for i in 1..qt_ciclos_gerar_w loop
		begin

		insert into sup_inv_ciclico_ciclo(
			nr_sequencia,
			dt_inicio,
			dt_fim,
			dt_atualizacao,
			nm_usuario,
			nr_seq_regra)
		values (	nextval('sup_inv_ciclico_ciclo_seq'),
			dt_inicial_ciclo_w,
			dt_final_ciclo_w,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_regra_p);

		dt_inicial_ciclo_w	:= dt_final_ciclo_w + 1;
		dt_final_ciclo_w	:= dt_inicial_ciclo_w + qt_dias_ciclos_p;

		end;
	end loop;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
