CREATE OR REPLACE PROCEDURE gerar_cid_eup_from_gv ( nm_usuario_p text, nr_seq_gestao_p text, nr_atendimento_p bigint, cd_medico_resp_p bigint) AS $body$
DECLARE


cd_cid_principal_w	gestao_vaga.cd_cid_principal%type;
dt_diagnostico_w	timestamp;


BEGIN

if (nr_seq_gestao_p IS NOT NULL AND nr_seq_gestao_p::text <> '') then

	begin
		select 	cd_cid_principal
		into STRICT 	cd_cid_principal_w
		from 	gestao_vaga
		where 	nr_sequencia = nr_seq_gestao_p
		and 	coalesce(nr_seq_agenda::text, '') = '';

	exception
	when others then
		cd_cid_principal_w := null;
	end;

	if (cd_cid_principal_w IS NOT NULL AND cd_cid_principal_w::text <> '') then

	dt_diagnostico_w := clock_timestamp();

		insert into  diagnostico_medico(
			nr_atendimento,
			dt_diagnostico,
			ie_tipo_diagnostico,
			cd_medico,
			dt_atualizacao,
			nm_usuario)
		values (
			nr_atendimento_p,
			dt_diagnostico_w,
			1,
			cd_medico_resp_p,
			clock_timestamp(),
			nm_usuario_p);

		insert into diagnostico_doenca(
			nr_atendimento,
			dt_diagnostico,
			cd_doenca,
			dt_atualizacao,
			nm_usuario)
		values (
			nr_atendimento_p,
			dt_diagnostico_w,
			cd_cid_principal_w,
			clock_timestamp(),
			nm_usuario_p);

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
