CREATE OR REPLACE PROCEDURE gerar_ciencia_recomendacao ( nr_prescricao_p text, cd_recomendacao_p text, nm_usuario_p text) AS $body$
DECLARE

 
ie_ciente_w		varchar(1);
nr_atendimento_w	bigint;
nr_seq_item_w		bigint;
nr_seq_alteracao_w	bigint;

c01 CURSOR FOR 
SELECT	nr_sequencia 
from	prescr_recomendacao 
where	nr_prescricao = nr_prescricao_p 
and	cd_recomendacao = cd_recomendacao_p;
					

BEGIN 
 
select	coalesce(max('S'),'N') 
into STRICT	ie_ciente_w 
from	prescr_recomendacao 
where	nr_prescricao = nr_prescricao_p 
and	cd_recomendacao = cd_recomendacao_p 
and	ie_ciente = 'S';
 
if (ie_ciente_w = 'S') then 
	--Ja foi registrada ciência para esta recomendação!'); 
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(261405);
else 
 
	select	max(nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from	prescr_medica 
	where	nr_prescricao	= nr_prescricao_p;
 
	update prescr_recomendacao 
	set	ie_ciente = 'S' 
	where	nr_prescricao = nr_prescricao_p 
	and	cd_recomendacao = cd_recomendacao_p;
	 
	open C01;
	loop 
	fetch C01 into 
		nr_seq_item_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		 
		select	nextval('prescr_mat_alteracao_seq') 
		into STRICT	nr_seq_alteracao_w 
		;
 
		insert	into prescr_mat_alteracao(nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_prescricao, 
			nr_seq_recomendacao, 
			nr_seq_horario_rec, 
			dt_alteracao, 
			cd_pessoa_fisica, 
			ie_alteracao, 
			ie_tipo_item, 
			dt_horario, 
			nr_atendimento, 
			cd_item) 
		VALUES (nr_seq_alteracao_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_prescricao_p, 
			nr_seq_item_w, 
			null, 
			clock_timestamp(), 
			obter_dados_usuario_opcao(nm_usuario_p,'C'), 
			7, 
			'R', 
			null, 
			nr_atendimento_w, 
			cd_recomendacao_p);
	 
	end loop;
	close C01;
	 
end if;
	 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
