SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_cirurgia_hl7 ( nr_atendimento_p bigint, nr_seq_interno_p bigint, cd_setor_atendimento_p bigint, ie_opcao_p text default 'I') AS $body$
DECLARE

 
				 
ie_leito_monit_w	varchar(1);
ie_leito_monit_ant_w	varchar(1);
ds_sep_bv_w		varchar(100)	:= obter_separador_bv;
ds_param_integ_hl7_w	varchar(4000);
cd_pessoa_fisica_w	varchar(15);
nr_seq_interno_old_w	bigint;
cd_setor_atendimento_w	integer;
ie_alta_setor_w		varchar(1) := 'N';
qt_setor_aberto_w	bigint;


BEGIN 
 
select	coalesce(max(nr_seq_interno),0) 
into STRICT	nr_seq_interno_old_w 
from	setor_atendimento a, 
		atend_paciente_unidade b 
where	b.nr_atendimento = nr_atendimento_p 
and		a.cd_setor_atendimento = b.cd_setor_atendimento 
and		coalesce(b.ie_passagem_setor,'N') = 'N' 
and		b.nr_seq_interno < nr_seq_interno_p;
 
 
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
into STRICT	ie_leito_monit_w 
from	atend_paciente_unidade a, 
		unidade_atendimento b 
where	a.cd_setor_atendimento 	= b.cd_setor_atendimento 
and		a.cd_unidade_compl	= b.cd_unidade_compl 
and		a.cd_unidade_basica	= b.cd_unidade_basica 
and		b.ie_leito_monitorado	= 'S' 
and		a.nr_seq_interno	= nr_seq_interno_p;
 
 
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
into STRICT	ie_leito_monit_ant_w 
from	atend_paciente_unidade a, 
		unidade_atendimento b 
where	a.cd_setor_atendimento 	= b.cd_setor_atendimento 
and		a.cd_unidade_compl	= b.cd_unidade_compl 
and		a.cd_unidade_basica	= b.cd_unidade_basica 
and		b.ie_leito_monitorado	= 'S' 
and		a.nr_seq_interno	= nr_seq_interno_old_w;
 
 
 
Select 	max(cd_setor_atendimento) 
into STRICT	cd_setor_Atendimento_w 
from	atend_paciente_unidade 
where	nr_seq_interno	= nr_seq_interno_old_w;
 
 
if	((nr_seq_interno_old_w = 0) or (ie_leito_monit_ant_w = 'N')) and (ie_opcao_p = 'I')then 
 
			 
		if (ie_leito_monit_w = 'S') then 
		 
			ds_param_integ_hl7_w := 'nr_atendimento=' || nr_atendimento_p || ds_sep_bv_w || 
									'nr_seq_interno=' || nr_seq_interno_p || ds_sep_bv_w|| 
									'cd_pessoa_fisica=' || obter_pessoa_atendimento(nr_atendimento_p,'C') || ds_sep_bv_w;
			CALL gravar_agend_integracao(67, ds_param_integ_hl7_w, cd_setor_atendimento_p);
			 
		end if;	
		 
elsif	((ie_leito_monit_w = 'S') or (ie_leito_monit_ant_w = 'S'))then 
 
	 
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
	into STRICT	ie_alta_setor_w 
	from	setor_atendimento				 
	where	cd_setor_atendimento = cd_setor_atendimento_w 
	and		coalesce(ie_rpa,'N') = 'N' 
	and		cd_classif_setor = 2;
	 
	 
	select	count(*) 
	into STRICT	qt_setor_aberto_w 
	from	setor_atendimento a, 
		atend_paciente_unidade b 
	where	b.nr_atendimento = nr_atendimento_p 
	and	a.cd_setor_atendimento = b.cd_setor_atendimento 
	and	coalesce(b.ie_passagem_setor,'N') = 'N' 
	and	(b.dt_saida_unidade IS NOT NULL AND b.dt_saida_unidade::text <> '') 
	and	a.cd_classif_setor <> 2	 
	and	coalesce(ie_rpa,'N') = 'N' 
	and	b.nr_seq_interno < nr_seq_interno_p;
	 
		 
	 
	if	((ie_alta_setor_w = 'S' AND qt_setor_aberto_w = 0) or (ie_leito_monit_ant_w = 'N')) and (ie_opcao_p = 'F') then 
 
 
		ds_param_integ_hl7_w := 'nr_atendimento=' || nr_atendimento_p || ds_sep_bv_w || 
						'nr_seq_interno=' || nr_seq_interno_p || ds_sep_bv_w|| 
						'cd_pessoa_fisica=' || obter_pessoa_atendimento(nr_atendimento_p,'C') || ds_sep_bv_w;
			CALL gravar_agend_integracao(69, ds_param_integ_hl7_w,cd_setor_atendimento_p);
	 
	else 
	 
 
		ds_param_integ_hl7_w := 'nr_atendimento=' || nr_atendimento_p || ds_sep_bv_w || 
					'nr_seq_interno=' || nr_seq_interno_p || ds_sep_bv_w || 
					'nr_seq_interno_old=' || nr_seq_interno_old_w || ds_sep_bv_w|| 
					'cd_pessoa_fisica=' || obter_pessoa_atendimento(nr_atendimento_p,'C') || ds_sep_bv_w;
		CALL gravar_agend_integracao(68, ds_param_integ_hl7_w, cd_setor_atendimento_p);
		 
		ds_param_integ_hl7_w := 'nr_atendimento=' || nr_atendimento_p || ds_sep_bv_w || 
					'nr_seq_interno=' || nr_seq_interno_p || ds_sep_bv_w || 
					'nr_seq_interno_old=' || nr_seq_interno_old_w || ds_sep_bv_w|| 
					'cd_pessoa_fisica=' || obter_pessoa_atendimento(nr_atendimento_p,'C') || ds_sep_bv_w;
		CALL gravar_agend_integracao(72, ds_param_integ_hl7_w, cd_setor_atendimento_p);	
		 
		 
	end if;
	 
	/*begin 
	commit; 
	exception 
	when others then 
		nr_seq_interno_old_w	:= null; 
	end;*/
 
 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
