CREATE OR REPLACE PROCEDURE gerar_cirurgia ( cd_estabelecimento_p bigint , nr_seq_agenda_p bigint, nm_usuario_p text, cd_setor_atendimento_p bigint ) AS $body$
DECLARE

 
nr_prescricao_w	bigint;		
ie_origem_inf_w	varchar(10);		
nr_cirurgia_w	bigint;			
							 

BEGIN 
 
 
CALL GERAR_CIRURGIA_AGENDA(cd_estabelecimento_p,nr_seq_agenda_p,nm_usuario_p,cd_setor_atendimento_p);
 
select 	max(obter_prescricao_agenda(a.nr_sequencia)), 
		max(nr_cirurgia) 
into STRICT	nr_prescricao_w, 
		nr_cirurgia_w 
from	agenda_paciente a 
where	nr_sequencia		= nr_seq_agenda_p;
 
if (coalesce(nr_prescricao_w::text, '') = '') and (nr_cirurgia_w IS NOT NULL AND nr_cirurgia_w::text <> '') then 
	 
	select	max(a.nr_prescricao) 
	into STRICT	nr_prescricao_w 
	from	cirurgia a 
	where	a.nr_cirurgia = nr_cirurgia_w;
	 
end if;
 
 
if (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then 
	SELECT 	coalesce(MAX('1'),'3') 
	INTO STRICT	ie_origem_inf_w 
	FROM	medico b, 
		usuario a 
	WHERE 	a.nm_usuario		= nm_usuario_p 
	AND	a.cd_pessoa_fisica	= b.cd_pessoa_fisica;
 
 
 
	CALL GERAR_PROC_AGENDA_CIRURGIA(nr_seq_agenda_p,nr_prescricao_w,nm_usuario_p);
	CALL GERAR_PRESCRICAO_CIRURGIA(0,0,0,nr_seq_agenda_p,ie_origem_inf_w,nm_usuario_p,nr_prescricao_w,null,null,900,obter_perfil_ativo,null,null,null,null,null);
	CALL GERAR_OPME_MATERIAS_ESP_AGENDA(nr_seq_agenda_p,nm_usuario_p,cd_estabelecimento_p);
	 
end if;
 
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
