SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_ci_risco_projeto ( nr_seq_risco_p bigint ) AS $body$
DECLARE


ds_comunicado_w			varchar(2000);
ds_titulo_w				varchar(80);
nm_coordenador_proj_w	varchar(20);
nm_gerente_proj_w		varchar(20);
nr_seq_proj_w			bigint;
ds_titulo_proj_w		varchar(80);


BEGIN

select	max(ds_titulo)
into STRICT	ds_titulo_w
from	proj_risco_implantacao
where	nr_sequencia = nr_seq_risco_p;

select	distinct(	select	x.nm_usuario
					from	usuario x,
							pessoa_fisica y
					where	x.cd_pessoa_fisica = y.cd_pessoa_fisica
					and		y.cd_pessoa_fisica = a.cd_coordenador)
into STRICT	nm_coordenador_proj_w
from	proj_projeto a,
		proj_risco_implantacao b,
		proj_risco_hist c
where	a.nr_sequencia = b.nr_seq_proj
and		b.nr_sequencia = nr_seq_risco_p;

select	distinct(	select	x.nm_usuario
					from	usuario x,
							pessoa_fisica y
					where	x.cd_pessoa_fisica = y.cd_pessoa_fisica
					and		y.cd_pessoa_fisica = a.cd_gerente_projeto)
into STRICT	nm_gerente_proj_w
from	proj_projeto a,
		proj_risco_implantacao b,
		proj_risco_hist c
where	a.nr_sequencia = b.nr_seq_proj
and		b.nr_sequencia = nr_seq_risco_p;

select	a.nr_sequencia,
		a.ds_titulo
into STRICT	nr_seq_proj_w,
		ds_titulo_proj_w
from	proj_projeto a,
		proj_risco_implantacao b
where	a.nr_sequencia = b.nr_seq_proj
and		b.nr_sequencia = nr_seq_risco_p;

if ((nm_coordenador_proj_w IS NOT NULL AND nm_coordenador_proj_w::text <> '') or (nm_gerente_proj_w IS NOT NULL AND nm_gerente_proj_w::text <> '')) then
	begin
	ds_comunicado_w	:= 	'Foi liberado um novo histórico de Risco de Projeto para o projeto: ' || nr_seq_proj_w || ' - ' || ds_titulo_proj_w || chr(13) || chr(10)
						|| chr(13) || chr(10) ||
						'Sequência do Risco: ' || nr_seq_risco_p || chr(13) || chr(10)
						|| chr(13) || chr(10) ||
						obter_desc_expressao(709094)/*'Risco: '*/
 || ds_titulo_w;
	if (nm_coordenador_proj_w IS NOT NULL AND nm_coordenador_proj_w::text <> '') then
		insert into comunic_interna(nr_sequencia,
									dt_comunicado,
									ds_titulo,
									ds_comunicado,
									nm_usuario,
									dt_atualizacao,
									ie_geral,
									nm_usuario_destino,
									ie_gerencial,
									nr_seq_classif,
									dt_liberacao)
							values (nextval('comunic_interna_seq'),
									clock_timestamp(),
									'Risco Projeto - Novo Histórico',
									ds_comunicado_w,
									'Tasy',
									clock_timestamp(),
									'N',
									nm_coordenador_proj_w,
									'N',
									null,
									clock_timestamp());
	end if;
	if (nm_gerente_proj_w IS NOT NULL AND nm_gerente_proj_w::text <> '') then
		insert into comunic_interna(nr_sequencia,
									dt_comunicado,
									ds_titulo,
									ds_comunicado,
									nm_usuario,
									dt_atualizacao,
									ie_geral,
									nm_usuario_destino,
									ie_gerencial,
									nr_seq_classif,
									dt_liberacao)
							values (nextval('comunic_interna_seq'),
									clock_timestamp(),
									'Risco Projeto - Novo Histórico',
									ds_comunicado_w,
									'Tasy',
									clock_timestamp(),
									'N',
									nm_gerente_proj_w,
									'N',
									null,
									clock_timestamp());
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE gerar_ci_risco_projeto ( nr_seq_risco_p bigint ) FROM PUBLIC;

