CREATE OR REPLACE PROCEDURE gerar_classif_etapa_proj ( nr_seq_cronograma_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_origem_w		bigint;
cd_classificacao_w	varchar(255);


BEGIN
if (nr_seq_cronograma_p IS NOT NULL AND nr_seq_cronograma_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	coalesce(min(nr_sequencia),0)
	into STRICT	nr_seq_origem_w
	from	proj_cron_etapa
	where	nr_seq_cronograma = nr_seq_cronograma_p
	and	coalesce(nr_seq_superior::text, '') = '';

	if (nr_seq_origem_w > 0) then
		begin
		cd_classificacao_w := '1';
		update	proj_cron_etapa
		set	cd_classificacao = cd_classificacao_w,
			nm_usuario = nm_usuario_p
		where	nr_sequencia = nr_seq_origem_w;

		CALL calcular_classif_etapa_proj(nr_seq_cronograma_p, nr_seq_origem_w, cd_classificacao_w, nm_usuario_p);
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
