SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE gerar_classif_lib_pf_auto ( cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE



nr_sequencia_w		bigint;
cd_perfil_w		integer := obter_perfil_ativo;
nr_dias_vig_final_w	bigint;
cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_dias_vig_final
	from	classif_pessoa_regra_lib b,
		classif_pessoa a
	where	a.nr_sequencia = b.nr_seq_classif_pessoa
	and	substr(obter_se_classif_pf_lib_usu(a.nr_sequencia, nm_usuario_p),1,1) = 'S'
	and	coalesce(b.ie_gerar_cad_pf,'N') = 'S'
	and (b.cd_perfil = cd_perfil_w or b.nm_usuario_lib = nm_usuario_p or b.cd_estab_lib = cd_estabelecimento_w)
	and	coalesce(b.cd_estab_lib,cd_estabelecimento_w) = cd_estabelecimento_w
	and	a.ie_situacao = 'A'
	and not exists (	SELECT 	1
				from 		pessoa_classif x
				where		a.nr_sequencia = x.nr_seq_classif
				and 		x.cd_pessoa_fisica = cd_pessoa_fisica_p
				and 		x.dt_inicio_vigencia <= clock_timestamp()
				and (coalesce(x.dt_final_vigencia::text, '') = '' or x.dt_final_vigencia >= clock_timestamp())
				and		coalesce(x.dt_inativacao::text, '') = ''
				and 		coalesce(x.cd_estabelecimento::text, '') = '');


BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_dias_vig_final_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into pessoa_classif(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_classif,
			cd_pessoa_fisica,
			dt_inicio_vigencia,
			dt_final_vigencia)
		values (	nextval('pessoa_classif_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_sequencia_w,
			cd_pessoa_fisica_p,
			clock_timestamp(),
			clock_timestamp() + nr_dias_vig_final_w);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
