CREATE OR REPLACE PROCEDURE gerar_classif_tiss_agenda (nr_atendimento_p bigint, nr_seq_agenda_p bigint, ie_opcao_p text) AS $body$
DECLARE

 
ie_tipo_retorno_w	smallint;


BEGIN 
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then 
	/* obter classificacao TISS */
 
	select	obter_classif_tiss_agenda(nr_seq_agenda_p, ie_opcao_p) 
	into STRICT	ie_tipo_retorno_w 
	;
 
	/* gerar classificacao atendimento */
 
	if (ie_opcao_p = 'C') then 
		update	atendimento_paciente 
		set	ie_tipo_consulta	= ie_tipo_retorno_w 
		where	nr_atendimento 	= nr_atendimento_p;
	elsif (ie_opcao_p = 'S') then 
		update	atendimento_paciente 
		set	ie_tipo_saida_consulta	= ie_tipo_retorno_w 
		where	nr_atendimento 		= nr_atendimento_p;
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
