CREATE OR REPLACE PROCEDURE gerar_cobranca_escritural_js ( nr_sequencia_p bigint, ie_parametro_p text, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
qt_existe_w	bigint;


BEGIN 
 
if (ie_parametro_p = 'S') then 
	 
	select	count(*) 
	into STRICT	qt_existe_w 
	from	pls_lote_mensalidade d, 
		pls_mensalidade c, 
		cobranca_escritural b, 
		titulo_receber_cobr a 
	where	a.nr_seq_cobranca = b.nr_sequencia 
	and	a.nr_seq_mensalidade = c.nr_sequencia 
	and	c.nr_seq_lote = d.nr_sequencia 
	and	d.nr_sequencia = nr_sequencia_p;
	 
	if (qt_existe_w > 0) then 
		--r.aise_application_error(-20011, obter_texto_tasy(103809, wheb_usuario_pck.get_nr_seq_idioma)); 
		CALL wheb_mensagem_pck.exibir_mensagem_abort(103809);
	end if;
 
end if;
 
CALL pls_gerar_cobranca_mensalidade(nr_sequencia_p, nm_usuario_p, cd_estabelecimento_p);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
