CREATE OR REPLACE PROCEDURE gerar_cobr_brasil_240_utl_file ( nr_seq_cobr_escrit_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
-- Febraban v08.4 - 01/09/2009 
						 
ds_conteudo_w			varchar(240);
ds_brancos_205_w		varchar(205);
ds_brancos_165_w		varchar(165);
nm_empresa_w			varchar(80);
nm_pessoa_w			varchar(80);
ds_brancos_54_w			varchar(54);
ds_endereco_w			varchar(40);
ds_bairro_w			varchar(40);
ds_brancos_80_w			varchar(80);
ds_banco_w			varchar(30);
ds_brancos_25_w			varchar(25);
cd_convenio_banco_w		varchar(20);
ds_cidade_w			varchar(20);
ds_nosso_numero_w		varchar(20);
ds_complemento_w		varchar(18);
vl_juros_w			varchar(17);
ds_brancos_17_w			varchar(17);
nr_conta_corrente_w		varchar(15);
dt_remessa_retorno_w		varchar(14);
dt_geracao_arquivo_w		varchar(14);
nr_inscricao_w			varchar(15);
vl_liquidacao_w			varchar(15);
vl_cobranca_w			varchar(15);
vl_desconto_w			varchar(15);
dt_desconto_w			varchar(8);
vl_mora_w			varchar(15);
nr_seq_registro_w		bigint;
ds_brancos_10_w			varchar(10);
qt_reg_lote_w			varchar(10);
dt_vencimento_w			varchar(8);
dt_emissao_w			varchar(8);
dt_pagamento_w			varchar(8);
cd_cep_w			varchar(8);
nr_seq_lote_w			varchar(7);
nr_seq_arquivo_w		varchar(6);
nr_seq_envio_w			varchar(8);
cd_agencia_bancaria_w		varchar(5);
ie_tipo_inscricao_w		varchar(5);
nr_endereco_w			varchar(5);
cd_banco_w			varchar(3);
ie_emissao_bloqueto_w		varchar(3);
nr_digito_agencia_w		varchar(2);
sg_uf_w				varchar(2);
ie_protesto_w			varchar(2);
ie_tipo_registro_w		varchar(1);
qt_reg_arquivo_w		bigint := 0;
ds_brancos_69_w			varchar(69);
ds_brancos_33_w			varchar(33);
dt_gravacao_w			varchar(8);
nr_nosso_numero_w		varchar(20);
nr_seq_carteira_cobr_w		varchar(1);
nr_documento_cobr_w		varchar(15);
nr_titulo_w			varchar(25);
nm_pessoa_titulo_w		varchar(40);
sg_estado_w			varchar(2);
nr_seq_apres_w			bigint	:= 1;
ds_brancos_9_w			varchar(9);
ds_brancos_2_w			varchar(2);
ds_zeros_23_w			varchar(23);
ds_brancos_8_w			varchar(8);
ds_brancos_55_w			varchar(55);
ds_brancos_28_w			varchar(28);

/*UTL File*/
 
arq_texto_w			utl_file.file_type;
ds_erro_w			varchar(255);
ds_local_w			varchar(255);
nm_arquivo_w			varchar(255);
ds_mensagem_w			varchar(255);

C01 CURSOR FOR 
	SELECT	lpad(c.cd_banco,3,'0') cd_banco, 
		substr(a.nr_sequencia,1,5) nr_seq_envio, 
		'3' ie_tipo_registro, 
		lpad(somente_numero(c.vl_liquidacao),15,'0') vl_liqudacao, 
		rpad('Banco do Brasil',30,' ') nm_cedente, 
		to_char(b.dt_pagamento_previsto,'ddmmyyyy') dt_vencimento, 
		lpad(somente_numero(c.vl_cobranca),15,'0') vl_cobranca, 
		lpad(somente_numero(c.vl_desconto),15,'0') vl_desconto, 
		to_char(clock_timestamp(),'ddmmyyyy') dt_desconto, 
		lpad(somente_numero(coalesce(c.vl_juros,0)) + somente_numero(coalesce(c.vl_multa,0)),15,'0') vl_mora, 
		to_char(c.dt_liquidacao,'ddmmyyyy') dt_pagamento, 
		rpad(substr(coalesce(d.cd_convenio_banco,e.cd_convenio_banco),1,7) || substr(lpad(c.nr_titulo,10,'0'),1,10),20,' ') ds_nosso_numero, 
		rpad(c.cd_agencia_bancaria,5,0) cd_agencia_bancaria, 
		rpad(calcula_digito('Modulo11',c.cd_agencia_bancaria),1,'0') nr_digito_agencia, 
		lpad(e.cd_conta,12,'0') || rpad(e.ie_digito_conta,1,'0') nr_conta_corrente, 
		rpad(b.nr_nosso_numero,20,' ') nr_nosso_numero, 
		lpad(b.nr_seq_carteira_cobr,1,'0') nr_seq_carteira_cobr, 
		rpad(a.nr_sequencia,15,' ') nr_documento_cobr, 
		to_char(b.dt_emissao,'ddmmyyyy') dt_emissao, 
		rpad(b.nr_titulo,25,' ') nr_titulo, 
		CASE WHEN coalesce(b.cd_pessoa_fisica::text, '') = '' THEN '2'  ELSE '1' END  ie_tipo_inscricao, 
		lpad(CASE WHEN coalesce(b.cd_pessoa_fisica::text, '') = '' THEN b.cd_cgc  ELSE (	SELECT	x.nr_cpf 								from	pessoa_fisica x 								where	x.cd_pessoa_fisica	= b.cd_pessoa_fisica) END ,15,'0') nr_inscricao, 
		rpad(upper(coalesce(elimina_caractere_especial(substr(obter_nome_pf_pj(b.cd_pessoa_fisica,b.cd_cgc),1,40)),' ')),40,' ') nm_pessoa_titulo, 
		rpad(upper(coalesce(elimina_caractere_especial(substr(obter_dados_pf_pj(b.cd_pessoa_fisica,b.cd_cgc,'EN'),1,40)),' ')),40,' ') ds_endereco, 
		rpad(upper(coalesce(elimina_caractere_especial(substr(obter_dados_pf_pj(b.cd_pessoa_fisica,b.cd_cgc,'B'),1,15)),' ')),15,' ') ds_bairro, 
		lpad(coalesce(elimina_caractere_especial(substr(obter_dados_pf_pj(b.cd_pessoa_fisica,b.cd_cgc,'CEP'),1,8)),' '),8,'0') cd_cep, 
		rpad(upper(coalesce(elimina_caractere_especial(substr(obter_dados_pf_pj(b.cd_pessoa_fisica,b.cd_cgc,'CI'),1,15)),' ')),15,' ') ds_cidade, 
		rpad(upper(coalesce(substr(obter_dados_pf_pj(b.cd_pessoa_fisica,b.cd_cgc,'UF'),1,2),' ')),2,' ') sg_estado 
	FROM banco_estabelecimento e, titulo_receber_cobr c, titulo_receber_v b, cobranca_escritural a
LEFT OUTER JOIN banco_carteira d ON (a.nr_seq_carteira_cobr = d.nr_sequencia)
WHERE a.nr_sequencia		= c.nr_seq_cobranca and c.nr_titulo		= b.nr_titulo and a.nr_seq_conta_banco	= e.nr_sequencia  and a.nr_sequencia		= nr_seq_cobr_escrit_p;


BEGIN 
nm_arquivo_w	:= to_char(clock_timestamp(),'ddmmyyyy') || to_char(clock_timestamp(),'hh24') || to_char(clock_timestamp(),'mi') || to_char(clock_timestamp(),'ss') || nm_usuario_p || '.rem';
 
SELECT * FROM obter_evento_utl_file(1, null, ds_local_w, ds_erro_w) INTO STRICT ds_local_w, ds_erro_w;
 
begin 
arq_texto_w := utl_file.fopen(ds_local_w,nm_arquivo_w,'W'); --arq_texto_w := utl_file.fopen('/srvfs03/FINANCEIRO/TASY/',nm_arquivo_w,'W'); 
exception
when others then 
	if (SQLSTATE = -29289) then 
		ds_erro_w := 'O acesso ao arquivo foi negado pelo sistema operacional (access_denied).';
	elsif (SQLSTATE = -29298) then 
		ds_erro_w := 'O arquivo foi aberto usando FOPEN_NCHAR, mas efetuaram-se operações de I/O usando funções nonchar comos PUTF ou GET_LINE (charsetmismatch).';
	elsif (SQLSTATE = -29291) then 
		ds_erro_w := 'Não foi possível apagar o arquivo (delete_failed).';
	elsif (SQLSTATE = -29286) then 
		ds_erro_w := 'Erro interno desconhecido no package UTL_FILE (internal_error).';
	elsif (SQLSTATE = -29282) then 
		ds_erro_w := 'O handle do arquivo não existe (invalid_filehandle).';
	elsif (SQLSTATE = -29288) then 
		ds_erro_w := 'O arquivo com o nome especificado não foi encontrado neste local (invalid_filename).';
	elsif (SQLSTATE = -29287) then 
		ds_erro_w := 'O valor de MAX_LINESIZE para FOPEN() é inválido; deveria estar na faixa de 1 a 32767 (invalid_maxlinesize).';
	elsif (SQLSTATE = -29281) then 
		ds_erro_w := 'O parâmetro open_mode na chamda FOPEN é inválido (invalid_mode).';
	elsif (SQLSTATE = -29290) then 
		ds_erro_w := 'O parâmetro ABSOLUTE_OFFSET para a chamada FSEEK() é inválido; deveria ser maior do que 0 e menor do que o número total de bytes do arquivo (invalid_offset).';
	elsif (SQLSTATE = -29283) then 
		ds_erro_w := 'O arquivo não pôde ser aberto ou operado da forma desejada - ou o caminho não foi encontrado (invalid_operation).';
	elsif (SQLSTATE = -29280) then 
		ds_erro_w := 'O caminho especificado não existe ou não está visível ao Oracle (invalid_path).';
	elsif (SQLSTATE = -29284) then 
		ds_erro_w := 'Não é possível efetuar a leitura do arquivo (read_error).';
	elsif (SQLSTATE = -29292) then 
		ds_erro_w := 'Não é possível renomear o arquivo.';
	elsif (SQLSTATE = -29285) then 
		ds_erro_w := 'Não foi possível gravar no arquivo (write_error).';
	else 
		ds_erro_w := 'Erro desconhecido no package UTL_FILE.';
	end if;	
	CALL wheb_mensagem_pck.exibir_mensagem_abort(186768,'DS_ERRO_W=' || ds_erro_w);
end;
--arq_texto_w := utl_file.fopen('/oraprd03/utlfile/',nm_arquivo_w,'W'); 
--\\192.168.0.230\UTLFILE 
 
update	cobranca_escritural 
set	ds_arquivo	= ds_local_w || nm_arquivo_w 
where	nr_sequencia	= nr_seq_cobr_escrit_p;
select	lpad(' ',17,' '), 
	lpad(' ',10,' '), 
	lpad(' ',54,' '), 
	lpad(' ',80,' '), 
	lpad(' ',25,' '), 
	lpad(' ',165,' '), 
	lpad(' ',205,' '), 
	lpad(' ',69,' '), 
	lpad(' ',33,' '), 
	lpad(' ',9,' '), 
	lpad(' ',2,' '), 
	lpad('0',23,'0'), 
	lpad(' ',8,' '), 
	lpad(' ',55,' '), 
	lpad(' ',28,' ') 
into STRICT	ds_brancos_17_w, 
	ds_brancos_10_w, 
	ds_brancos_54_w, 
	ds_brancos_80_w, 
	ds_brancos_25_w, 
	ds_brancos_165_w, 
	ds_brancos_205_w, 
	ds_brancos_69_w, 
	ds_brancos_33_w, 
	ds_brancos_9_w, 
	ds_brancos_2_w, 
	ds_zeros_23_w, 
	ds_brancos_8_w, 
	ds_brancos_55_w, 
	ds_brancos_28_w
;
 
/* Header Arquivo*/
 
select	'0' ie_tipo_registro, 
	lpad(c.cd_banco,3,'0') cd_banco, 
	to_char(clock_timestamp(),'DDMMYYYYHHMISS') dt_geracao_arquivo, 
	rpad('BANCO DO BRASIL S.A.',30,' ') ds_banco, 
	lpad(c.cd_agencia_bancaria,5,0) cd_agencia_bancaria, 
	rpad(calcula_digito('Modulo11',c.cd_agencia_bancaria),1,'0') nr_digito_agencia, 
	lpad(c.cd_conta,12,'0') || rpad(c.ie_digito_conta,1,'0') nr_conta_corrente, 
	rpad(upper(elimina_caractere_especial(substr(obter_razao_social(b.cd_cgc),1,30))),30,' ') nm_empresa, 
	lpad(b.cd_cgc,14,'0') nr_inscricao, 
	rpad(coalesce(d.cd_convenio_banco,c.cd_convenio_banco),20,'0') cd_convenio_banco, 
	lpad(a.nr_sequencia,6,'0') 
into STRICT	ie_tipo_registro_w, 
	cd_banco_w, 
	dt_geracao_arquivo_w, 
	ds_banco_w, 
	cd_agencia_bancaria_w, 
	nr_digito_agencia_w, 
	nr_conta_corrente_w, 
	nm_empresa_w, 
	nr_inscricao_w, 
	cd_convenio_banco_w, 
	nr_seq_arquivo_w 
FROM banco_estabelecimento c, estabelecimento b, cobranca_escritural a
LEFT OUTER JOIN banco_carteira d ON (a.nr_seq_carteira_cobr = d.nr_sequencia)
WHERE a.cd_estabelecimento	= b.cd_estabelecimento and a.nr_seq_conta_banco	= c.nr_sequencia  and a.nr_sequencia		= nr_seq_cobr_escrit_p;
 
ds_conteudo_w	:=	cd_banco_w || '0000' || ie_tipo_registro_w || ds_brancos_9_w || '2' || nr_inscricao_w || cd_convenio_banco_w ||  
			cd_agencia_bancaria_w || nr_digito_agencia_w || nr_conta_corrente_w || ' ' || nm_empresa_w || ds_banco_w || 
			ds_brancos_10_w || '1' || dt_geracao_arquivo_w || nr_seq_arquivo_w || '08400000' || ds_brancos_69_w;
 
utl_file.put_line(arq_texto_w,ds_conteudo_w);
utl_file.fflush(arq_texto_w);
 
qt_reg_arquivo_w	:= qt_reg_arquivo_w + 1;
/* Fim Header Arquivo */
 
 
/* Header Lote */
 
select	'1' ie_tipo_registro, 
	lpad(c.cd_banco,3,'0') cd_banco, 
	lpad(a.nr_sequencia,8,'0') nr_seq_envio, 
	rpad(c.cd_agencia_bancaria,5,0) cd_agencia_bancaria, 
	rpad(calcula_digito('Modulo11',c.cd_agencia_bancaria),1,'0') nr_digito_agencia, 
	lpad(c.cd_conta,12,'0') || rpad(c.ie_digito_conta,1,'0') nr_conta_corrente, 
	rpad(upper(elimina_caractere_especial(substr(obter_razao_social(b.cd_cgc),1,30))),30,' ') nm_empresa, 
	lpad(b.cd_cgc,15,'0') nr_inscricao, 
	rpad(coalesce(d.cd_convenio_banco,c.cd_convenio_banco),20,'0') cd_convenio_banco, 
	rpad(a.nr_sequencia,6,'0'), 
	rpad(obter_dados_pf_pj(null, b.cd_cgc,'EN'),40,' '), 
	rpad(obter_dados_pf_pj(null, b.cd_cgc,'NR'),5,' '), 
	rpad(obter_dados_pf_pj(null, b.cd_cgc,'CM'),18,' '), 
	rpad(obter_dados_pf_pj(null, b.cd_cgc,'MU'),20,' '), 
	rpad(obter_dados_pf_pj(null, b.cd_cgc,'CEP'),8,' '), 
	rpad(obter_dados_pf_pj(null, b.cd_cgc,'UF'),2,' '), 
	to_char(clock_timestamp(),'DDMMYYYY') dt_gravacao 
into STRICT	ie_tipo_registro_w, 
	cd_banco_w, 
	nr_seq_envio_w, 
	cd_agencia_bancaria_w, 
	nr_digito_agencia_w, 
	nr_conta_corrente_w, 
	nm_empresa_w, 
	nr_inscricao_w, 
	cd_convenio_banco_w, 
	nr_seq_arquivo_w, 
	ds_endereco_w, 
	nr_endereco_w, 
	ds_complemento_w, 
	ds_cidade_w, 
	cd_cep_w, 
	sg_uf_w, 
	dt_gravacao_w 
FROM banco_estabelecimento c, estabelecimento b, cobranca_escritural a
LEFT OUTER JOIN banco_carteira d ON (a.nr_seq_carteira_cobr = d.nr_sequencia)
WHERE a.cd_estabelecimento	= b.cd_estabelecimento and a.nr_seq_conta_banco	= c.nr_sequencia  and a.nr_sequencia		= nr_seq_cobr_escrit_p;
 
ds_conteudo_w	:= 	cd_banco_w || '0001' || ie_tipo_registro_w || 'R0100043 2' || nr_inscricao_w || cd_convenio_banco_w || 
			cd_agencia_bancaria_w || nr_digito_agencia_w || nr_conta_corrente_w || ' ' || nm_empresa_w || ds_brancos_80_w || 
			ds_zeros_23_w || ds_brancos_33_w;
 
nr_seq_apres_w	:= nr_seq_apres_w + 1;
 
utl_file.put_line(arq_texto_w,ds_conteudo_w);
utl_file.fflush(arq_texto_w);
 
qt_reg_arquivo_w	:= qt_reg_arquivo_w + 1;
/* Fim Header Lote */
 
 
/* Detalhe */
 
open C01;
loop 
fetch C01 into	 
	cd_banco_w, 
	nr_seq_envio_w, 
	ie_tipo_registro_w, 
	vl_liquidacao_w, 
	ds_banco_w, 
	dt_vencimento_w, 
	vl_cobranca_w, 
	vl_desconto_w, 
	dt_desconto_w, 
	vl_mora_w, 
	dt_pagamento_w, 
	ds_nosso_numero_w, 
	cd_agencia_bancaria_w, 
	nr_digito_agencia_w, 
	nr_conta_corrente_w, 
	nr_nosso_numero_w, 
	nr_seq_carteira_cobr_w, 
	nr_documento_cobr_w, 
	dt_emissao_w, 
	nr_titulo_w, 
	ie_tipo_inscricao_w, 
	nr_inscricao_w, 
	nm_pessoa_titulo_w, 
	ds_endereco_w, 
	ds_bairro_w, 
	cd_cep_w, 
	ds_cidade_w, 
	sg_estado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
 
	nr_seq_registro_w	:= coalesce(nr_seq_registro_w,0) + 1;
 
	/* Segmento P */
 
	nr_seq_apres_w		:= nr_seq_apres_w + 1;
 
	ds_conteudo_w	:=	cd_banco_w || '0001' || ie_tipo_registro_w || lpad(nr_seq_registro_w,5,'0') || 'P' || ' ' || '00' || 
				cd_agencia_bancaria_w || nr_digito_agencia_w || nr_conta_corrente_w || ' ' || ds_nosso_numero_w || 
				nr_seq_carteira_cobr_w || '1' || '2' || '2' || '2' || nr_documento_cobr_w || dt_vencimento_w || 
				vl_cobranca_w || cd_agencia_bancaria_w || nr_digito_agencia_w || '99' || 'A' || dt_emissao_w || '1' || 
				ds_brancos_8_w || vl_mora_w || '1' || dt_desconto_w || vl_desconto_w || '000000000000000' || '000000000000000' || 
				nr_titulo_w || '3' || '00' || '1' || '000' || '09' || '0000000000' || ' ';
	 
	utl_file.put_line(arq_texto_w,ds_conteudo_w);
	utl_file.fflush(arq_texto_w);
	/* Fim segmento P */
 
 
	/* Segmento Q */
 
	nr_seq_apres_w	:= nr_seq_apres_w + 1;
 
	ds_conteudo_w	:= 	cd_banco_w || '0001' || ie_tipo_registro_w || lpad(nr_seq_registro_w,5,'0') || 'Q' || ' ' || '00' || 
				ie_tipo_inscricao_w || nr_inscricao_w || nm_pessoa_titulo_w || ds_endereco_w || ds_bairro_w || cd_cep_w || 
				ds_cidade_w || sg_estado_w || '0' || ds_brancos_55_w || '000' || ds_brancos_28_w;
 
	insert into w_envio_banco(	nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			cd_estabelecimento, 
			ds_conteudo, 
			nr_seq_apres) 
	values (	nextval('w_envio_banco_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			cd_estabelecimento_p, 
			ds_conteudo_w, 
			nr_seq_apres_w);
 
	/* Fim segmento Q */
 
				 
	qt_reg_arquivo_w	:= qt_reg_arquivo_w + 1;
	 
end loop;
close C01;
/* Fim detalhe */
 
 
/* Trailler Lote*/
 
select	count(*), 
	'9' ie_tipo_registro, 
	'99999' nr_seq_envio, 
	somente_numero(to_char(sum(b.vl_liquidacao),'000000000000.00')) 
into STRICT	qt_reg_lote_w, 
	ie_tipo_registro_w, 
	nr_seq_envio_w, 
	vl_liquidacao_w 
from	titulo_receber_cobr b, 
	cobranca_escritural a 
where	a.nr_sequencia		= b.nr_seq_cobranca 
and	a.nr_sequencia		= nr_seq_cobr_escrit_p 
group by a.nr_sequencia, 
	 a.dt_remessa_retorno, 
	 a.dt_remessa_retorno, 
	 a.cd_banco, 
	a.ie_emissao_bloqueto;
	 
ds_conteudo_w	:= 	'001' || nr_seq_envio_w || '5' || ds_brancos_9_w || lpad(qt_reg_lote_w,6,'0') || '000000' || lpad(vl_liquidacao_w,16,'0') || 
			'000000000000000000' || ds_brancos_165_w || '0000000000';
 
nr_seq_apres_w	:= nr_seq_apres_w + 1;
	 
utl_file.put_line(arq_texto_w,ds_conteudo_w);
utl_file.fflush(arq_texto_w);
		 
qt_reg_arquivo_w	:= qt_reg_arquivo_w + 1;
/* Fim Trailler Lote*/
 
 
/* Trailler Arquivo*/
 
select	count(*), 
	'9' ie_tipo_registro, 
	'99999' nr_seq_envio, 
	somente_numero(to_char(sum(b.vl_liquidacao),'000000000000.00')) 
into STRICT	qt_reg_lote_w, 
	ie_tipo_registro_w, 
	nr_seq_envio_w, 
	vl_liquidacao_w 
from	titulo_receber_cobr b, 
	cobranca_escritural a 
where	a.nr_sequencia		= b.nr_seq_cobranca 
and	a.nr_sequencia		= nr_seq_cobr_escrit_p 
group by 
	a.nr_sequencia, 
	a.dt_remessa_retorno, 
	a.dt_remessa_retorno, 
	a.cd_banco, 
	a.ie_emissao_bloqueto;
	 
qt_reg_arquivo_w := qt_reg_arquivo_w + 1;
 
ds_conteudo_w	:=	'001' || '9999' || '9' || ds_brancos_9_w || '000001' || lpad(qt_reg_arquivo_w,6,'0') || '000000' || 
			ds_brancos_205_w;
 
nr_seq_apres_w	:= nr_seq_apres_w + 1;
 
utl_file.put_line(arq_texto_w,ds_conteudo_w);
utl_file.fflush(arq_texto_w);
/* Fim Trailler Arquivo*/
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
