CREATE OR REPLACE PROCEDURE gerar_colunas_relat_doc ( nr_seq_relat_p bigint, nr_seq_doc_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_banda_w		bigint;
nr_seq_campo_w		bigint;
nm_atributo_w		varchar(50);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	banda_relatorio
	where	nr_seq_relatorio = nr_seq_relat_p;

C02 CURSOR FOR
	SELECT	nr_sequencia,
		nm_atributo
	from	banda_relat_campo
	where	nr_seq_banda = nr_seq_banda_w
	and	ie_tipo_campo not in (1,2,3,14,16);


BEGIN

if (coalesce(ie_acao_p,'I') = 'I') then

	open C01;
	loop
	fetch C01 into
		nr_seq_banda_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		open C02;
		loop
		fetch C02 into
			nr_seq_campo_w,
			nm_atributo_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin

			insert into relat_informacao_coluna(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_documentacao,
				nr_seq_relat,
				nr_seq_banda,
				nm_atributo)
			values (	nextval('relat_informacao_coluna_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_doc_p,
				nr_seq_relat_p,
				nr_seq_banda_w,
				nm_atributo_w);

			end;
		end loop;
		close C02;

		end;
	end loop;
	close C01;

elsif (coalesce(ie_acao_p,'I') = 'E') then

	delete	FROM relat_informacao_coluna
	where	nr_seq_documentacao = nr_seq_doc_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
