CREATE OR REPLACE PROCEDURE gerar_componentes_opm ( cd_material_p bigint, nr_seq_opm_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_componente_w	bigint;
qt_material_w	double precision;

c01 CURSOR FOR
	SELECT	b.cd_material,
		coalesce(coalesce(coalesce(b.qt_fixa,b.qt_dose), b.qt_material),1)
	from   	ficha_tecnica a,
		ficha_tecnica_componente b
	where  	a.nr_ficha_tecnica = b.nr_ficha_tecnica
	and	coalesce(a.ie_situacao,'A') 	= 'A'
	and	coalesce(b.ie_situacao,'A') 	= 'A'
	and	(b.cd_material IS NOT NULL AND b.cd_material::text <> '')
	and	a.ie_tipo_ficha = 'O'
   	and   	a.cd_estabelecimento = cd_estabelecimento_p
	and	a.cd_material 	  	= cd_material_p;


BEGIN

delete from prescr_opm_componente where nr_seq_opm = nr_seq_opm_p;

open C01;
loop
fetch C01 into
	cd_componente_w,
	qt_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert	into prescr_opm_componente(	nr_sequencia,
						dt_atualizacao,
						nm_usuario,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						cd_material,
						cd_material_padrao,
						nr_seq_opm,
						qt_material)
	values (	nextval('prescr_opm_componente_seq'),
						clock_timestamp(),
						nm_usuario_p,
						clock_timestamp(),
						nm_usuario_p,
						cd_componente_w,
						cd_componente_w,
						nr_seq_opm_p,
						qt_material_w);
	end;
end loop;
close C01;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
