CREATE OR REPLACE PROCEDURE gerar_componente_lote_producao ( nr_lote_producao_p bigint, cd_material_p bigint, qt_material_p bigint, cd_local_estoque_p bigint, nr_seq_lote_fornec_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_sequencia_w			integer;
cd_material_w			integer;
cd_unidade_medida_estoque_w	varchar(30);
cd_unidade_medida_consumo_w	varchar(30);
dt_validade_lote_w			timestamp;


BEGIN

select	substr(obter_dados_material(cd_material_p,'UME'),1,5),
	substr(obter_dados_material(cd_material_p,'UMC'),1,5)
into STRICT	cd_unidade_medida_estoque_w,
	cd_unidade_medida_consumo_w
;

select (coalesce(max(nr_sequencia),0) + 1)
into STRICT	nr_sequencia_w
from	lote_producao_comp
where	nr_lote_producao = nr_lote_producao_p;

if (coalesce(nr_seq_lote_fornec_p,0) > 0) then
	select	to_date(substr(obter_dados_lote_fornec(nr_seq_lote_fornec_p,'V'),1,15),'DD/MM/YYYY')
	into STRICT	dt_validade_lote_w
	;

end if;

insert into lote_producao_comp(
	nr_lote_producao,
	nr_sequencia,
	cd_material,
	cd_unidade_medida,
	cd_unid_med_etq,
	cd_local_estoque,
	dt_atualizacao,
	nm_usuario,
	qt_prevista,
	nr_seq_lote_fornec,
	dt_validade)
values (	nr_lote_producao_p,
	nr_sequencia_w,
	cd_material_p,
	cd_unidade_medida_consumo_w,
	cd_unidade_medida_estoque_w,
	cd_local_estoque_p,
	clock_timestamp(),
	nm_usuario_p,
	qt_material_p,
	CASE WHEN nr_seq_lote_fornec_p='0' THEN null  ELSE nr_seq_lote_fornec_p END ,
	dt_validade_lote_w);

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
