CREATE OR REPLACE PROCEDURE gerar_comp_adicional_rec_real ( nr_seq_cardapio_p bigint, nr_seq_gen_alim_p bigint, qt_gramas_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
qt_refeicao_real_w	bigint;
nr_seq_receita_w	bigint;
qt_refeicao_w		bigint;
qt_componente_w		double precision;


BEGIN

select	nr_sequencia,
	nr_seq_receita,
	coalesce(qt_refeicao,1)
into STRICT	nr_sequencia_w,
	nr_seq_receita_w,
	qt_refeicao_real_w
from	nut_receita_real
where	nr_seq_cardapio = nr_seq_cardapio_p;

select	coalesce(max(a.qt_refeicao),1)
into STRICT	qt_refeicao_w
from	nut_receita_comp b,
	nut_receita a
where	a.nr_sequencia	= nr_seq_receita_w
and	b.nr_seq_receita	= a.nr_sequencia
and exists (	SELECT	1
		from	nut_rec_real_comp c,
			nut_receita_real d
		where   c.nr_seq_rec_real = d.nr_sequencia
		and     d.nr_seq_cardapio = nr_seq_cardapio_p
		and	coalesce(c.nr_seq_gen_alim_sub,c.nr_seq_gen_alim) = b.nr_seq_gen_alim);

--qt_componente_w	:= qt_gramas_p * qt_refeicao_real_w / qt_refeicao_w;
INSERT INTO nut_rec_real_comp(nr_sequencia,
		nr_seq_rec_real,
		nr_seq_gen_alim,
		dt_atualizacao,
		nm_usuario,
		qt_componente,
		vl_custo,
		ie_comp_adicional)
	VALUES (nextval('nut_rec_real_comp_seq'),
		nr_sequencia_w,
		nr_seq_gen_alim_p,
		clock_timestamp(),
		nm_usuario_p ,
		qt_gramas_p,
		0,
		'S');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
