CREATE OR REPLACE PROCEDURE gerar_comp_kit_estoque ( cd_estabelecimento_p bigint, nr_seq_kit_estoque_p bigint, cd_material_p bigint, qt_material_p bigint, nr_seq_lote_fornec_p bigint, cd_local_estoque_p bigint, nm_usuario_p text, cd_kit_material_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
 -- Para kit (kit_estoque)
DECLARE



cd_kit_material_w		integer;
nr_sequencia_w			bigint;
nr_seq_item_kit_w		integer;
nr_seq_kit_estoque_w		bigint;	
nr_seq_kit_material_w		bigint;	
cd_material_w			integer;
qt_material_w			double precision;
qt_saldo_w			double precision;
qt_material_ww			double precision;
qt_restante_ww			double precision;
nr_seq_kit_estoque_comp_w 	bigint;
qt_material_prescr_w		double precision;
qt_material_subst_w		integer;
nr_seq_devolucao_w		bigint;
qt_material_atendido_w		double precision;
cd_material_atendido_w		integer;
nr_seq_lote_fornec_atendido_w	bigint;
nr_seq_kit_estoque_atendido_w	bigint;
qt_material_consistido_w	double precision;
ie_gerado_barras_w 		varchar(1);
nr_seq_reg_kit_w		bigint;
nr_seq_reg_kit_ant_w		bigint := 0;
ds_retorno_w			varchar(255):= null;
ds_retorno_ww			varchar(255):= null;

C01 CURSOR FOR
SELECT	nr_sequencia,
	coalesce(qt_material,0)
from	componente_kit
where	ie_situacao = 'A'
and (cd_material = cd_material_p or obter_material_generico(cd_material) = obter_material_generico(cd_material_p))
and	cd_kit_material = cd_kit_material_w;
	

BEGIN	
select	cd_kit_material
into STRICT	cd_kit_material_w
from 	kit_estoque
where 	nr_sequencia = nr_seq_kit_estoque_p;

if (coalesce(nr_sequencia_p,0) = 0) then
	begin	
	select	nextval('kit_estoque_seq')
	into STRICT	nr_seq_kit_estoque_w
	;
	
	insert 	into kit_estoque(
		nr_sequencia,
		cd_kit_material,
		dt_atualizacao,
		nm_usuario,
		dt_montagem,
		nm_usuario_montagem,
		dt_utilizacao,           
		nm_usuario_util,
		nr_cirurgia,
		cd_estabelecimento,
		cd_medico,
		nr_prescricao,
		nr_atendimento,
		cd_local_estoque,
		ie_status,
		nr_seq_kit_origem)
	values (nr_seq_kit_estoque_w,
		cd_kit_material_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		null,
		null,
		null,
		cd_estabelecimento_p,
		null,
		null,
		null,
		cd_local_estoque_p,
		null,
		nr_seq_kit_estoque_p);
	end;
else
	 nr_seq_kit_estoque_w := nr_sequencia_p;
end if;	

qt_restante_ww	:= qt_material_p;

open C01;
loop
fetch C01 into	
	nr_seq_item_kit_w,
	qt_material_w;
exit when ((C01%notfound) or (qt_restante_ww = 0));
	begin
	select	coalesce(sum(qt_material),0)
	into STRICT	qt_material_ww
	from	kit_estoque_comp
	where	nr_seq_kit_estoque = nr_seq_kit_estoque_w
	and	nr_seq_item_kit = nr_seq_item_kit_w;
	
	qt_saldo_w := (qt_material_w - qt_material_ww);
	
	if (qt_saldo_w > 0) then
		begin
		if (qt_saldo_w >= qt_restante_ww) then
			qt_material_ww	:= qt_restante_ww;
		else
			qt_material_ww	:= qt_saldo_w;
		end if;
		
		qt_restante_ww	:= (qt_restante_ww - qt_material_ww);
		
		select	coalesce(max(nr_sequencia),0) + 1
		into STRICT	nr_sequencia_w
		from	kit_estoque_comp
		where	nr_seq_kit_estoque = nr_seq_kit_estoque_w;

		insert into kit_estoque_comp(
			nr_seq_kit_estoque,
			nr_sequencia,
			cd_material,
			dt_atualizacao,
			nm_usuario,
			qt_material,
			ie_gerado_barras,
			nr_seq_lote_fornec,
			nr_seq_item_kit)
		values (	nr_seq_kit_estoque_w,
			nr_sequencia_w,
			cd_material_p,
			clock_timestamp(),
			nm_usuario_p,
			qt_material_ww,
			'S',
			CASE WHEN coalesce(nr_seq_lote_fornec_p,'0')='0' THEN  null  ELSE nr_seq_lote_fornec_p END ,
			nr_seq_item_kit_w);
		end;
	end if;
	end;
end loop;
close C01;

if (qt_restante_ww > 0) then
	begin
	select	coalesce(max(nr_sequencia),0) + 1
	into STRICT	nr_sequencia_w
	from	kit_estoque_comp
	where	nr_seq_kit_estoque = nr_seq_kit_estoque_w;

	insert into kit_estoque_comp(
		nr_seq_kit_estoque,
		nr_sequencia,
		cd_material,
		dt_atualizacao,
		nm_usuario,
		qt_material,
		ie_gerado_barras,
		nr_seq_lote_fornec,
		nr_seq_item_kit)
	values (	nr_seq_kit_estoque_w,
		nr_sequencia_w,
		cd_material_p,
		clock_timestamp(),
		nm_usuario_p,
		qt_restante_ww,
		'S',
		CASE WHEN coalesce(nr_seq_lote_fornec_p,'0')='0' THEN  null  ELSE nr_seq_lote_fornec_p END ,
		null);
	end;
end if;

commit;

cd_kit_material_p	:= cd_kit_material_w;
nr_sequencia_p		:= nr_seq_kit_estoque_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
